mapper.select:
		select `tid`,`transferred_account`,`transfer_account`,`money`,`settlement_method`,`settlement_number`,`remarks`,`banktransferslip_id`,`del_flag` from cus_banktransferslip_item

		  		   and tid = #{tid} 
		  		   and transferred_account = #{transferredAccount} 
		  		   and transfer_account = #{transferAccount} 
		  		   and money = #{money} 
		  		   and settlement_method = #{settlementMethod} 
		  		   and settlement_number = #{settlementNumber} 
		  		   and remarks = #{remarks} 
		  		   and banktransferslip_id = #{banktransferslipId} 
		  		   and del_flag = #{delFlag} 



                order by ${sort} ${order}


                order by tid desc



			limit #{offset}, #{limit}

select count(*) from cus_banktransferslip_item

  		   and tid = #{tid} 
  		   and transferred_account = #{transferredAccount} 
  		   and transfer_account = #{transferAccount} 
  		   and money = #{money} 
  		   and settlement_method = #{settlementMethod} 
