mapper.select:
		select `id`,`transfer_date`,`handle_people`,`transfer_num`,`audit_status`,`auditor`,`auditor_time`,`create_by`,`create_time`,`update_by`,`update_time`,`del_flag` from cus_banktransferslip

		  		   and id = #{id} 
		  		   and transfer_date = #{transferDate} 
		  		   and handle_people = #{handlePeople} 
		  		   and transfer_num = #{transferNum} 
		  		   and audit_status = #{auditStatus} 
		  		   and LEFT(inhead_code,12) = #{maxNo} 
		  		   and auditor = #{auditor} 
		  		   and auditor_time = #{auditorTime} 
		  		   and create_by = #{createBy} 
		  		   and create_time = #{createTime} 
		  		   and update_by = #{updateBy} 
		  		   and update_time = #{updateTime} 
		  		   and del_flag = #{delFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from cus_banktransferslip

  		   and id = #{id} 
  		   and transfer_date = #{transferDate} 
  		   and handle_people = #{handlePeople} 
  		   and transfer_num = #{transferNum} 
  		   and audit_status = #{auditStatus} 
  		   and auditor = #{auditor} 
  		   and auditor_time = #{auditorTime} 
  		   and create_by = #{createBy} 
  		   and create_time = #{createTime} 
  		   and update_by = #{updateBy} 
  		   and update_time = #{updateTime} 
  		   and del_flag = #{delFlag}

		select banktranslip.`id`,`transfer_date` as `transferDate`,userh.username as `handlePeople`,`transfer_num` as `transferNum`,dict.name as `auditStatus`,
			   usera.username as `auditor`,`auditor_time` as `auditorTime`,userc.username as `createBy`,banktranslip.`create_time` as `createTime`,useru.username as `updateBy`,
			   banktranslip.`update_time` as `updateTime`,banktranslip.`del_flag` as `delFlag`,`tid`,`transferred_account` as `transferredAccount`,`transfer_account` as 
			   `transferAccount`,`money`,dicts.name as `settlementMethod`,`settlement_number` as `settlementNumber`,`remarks`,`banktransferslip_id` as `banktransferslipId`,
			   banktranslipitem.`del_flag` as `delFlag` 
			from cus_banktransferslip banktranslip left join cus_banktransferslip_item banktranslipitem on banktranslip.id = banktranslipitem.`banktransferslip_id`
				 left join cus_dictionary dict on banktranslip.audit_status = dict.id
				 left join sys_user usera on banktranslip.auditor = usera.user_id left join sys_user userc on banktranslip.create_by = userc.user_id 
				 left join sys_user useru on banktranslip.update_by = useru.user_id left join cus_dictionary dicts on banktranslipitem.settlement_method = dicts.id
				 left join sys_user userh on banktranslip.handle_people = userh.user_id

		  		    and  DATE_FORMAT(transfer_date,'%Y-%m-%d') >=  DATE_FORMAT(#{startTime},'%Y-%m-%d')
				    and  DATE_FORMAT(transfer_date,'%Y-%m-%d') <=  DATE_FORMAT(#{endTime},'%Y-%m-%d')
				   and transfer_num = #{transferNum} 
				   and handle_people = #{handlePeople} 
				   and audit_status = #{auditStatus} 
		  		   and del_flag = #{delFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from cus_banktransferslip banktranslip left join cus_banktransferslip_item banktranslipitem on banktranslip.id = banktranslipitem.`banktransferslip_id`
		 left join cus_dictionary dict on banktranslip.audit_status = dict.id
		 left join sys_user usera on banktranslip.auditor = usera.user_id left join sys_user userc on banktranslip.create_by = userc.user_id 
		 left join sys_user useru on banktranslip.update_by = useru.user_id left join cus_dictionary dicts on banktranslipitem.settlement_method = dicts.id
		 left join sys_user userh on banktranslip.handle_people = userh.user_id

  		    and  DATE_FORMAT(transfer_date,'%Y-%m-%d') >=  DATE_FORMAT(#{startTime},'%Y-%m-%d')
		    and  DATE_FORMAT(transfer_date,'%Y-%m-%d') <=  DATE_FORMAT(#{endTime},'%Y-%m-%d')
		   and transfer_num = #{transferNum} 
		   and handle_people = #{handlePeople} 
		   and audit_status = #{auditStatus} 
  		   and del_flag = #{delFlag}
