	 and del_flag = #{delFlag} 
	 and status = #{status} 



		order by ${sort} ${order}


		order by id desc



	limit #{offset}, #{limit}

select count(*) from cus_daily_report

	 and id = #{id} 
	 and com_content = #{comContent} 
	 and uncom_content = #{uncomContent} 
	 and need_concert_content = #{needConcertContent} 
	 and wait_contet = #{waitContet} 
	 and must_contet = #{mustContet} 
	 and create_by = #{createBy} 
	 and create_time = #{createTime} 
	 and update_by = #{updateBy} 
	 and update_time = #{updateTime} 
	 and del_flag = #{delFlag} 
	 and status = #{status}

SELECT

	case when uassoc.sign=1 then 3
	when uassoc.sign is null then 1
	end sign,
	case when uassoc.sign=1 then '已读'
	when uassoc.sign is null then '未读'
	end readStatus,
	uassoc.user_id as userId,

daily.id as id,
daily.com_content as comContent,
daily.uncom_content as uncomCOntent,
daily.need_concert_content as needConcertContent,
daily.wait_contet as waitContent,
daily.must_contet as mustContent,
daily.create_by as createBy,
daily.create_time as createTime,
daily.update_by as updateBy,
daily.update_time as updateTime,
daily.del_flag as delFlag,
daily.status as status,
b.name as `statusName`,
dept.dept_id as deptId,
dept.id_path as idPath,
dept.name as deptName,
employee.name as userName
FROM
cus_daily_report daily
LEFT JOIN sys_user employee ON employee.user_id = daily.create_by
LEFT JOIN cus_dictionary b ON b.id = daily.status
LEFT JOIN sys_dept dept ON dept.dept_id = employee.dept_id
 LEFT JOIN cus_user_assoc uassoc on uassoc.assoc_id = daily.id and uassoc.assoc_type = 'DAILY_REPORT_TARGET'
LEFT JOIN view_daliyReport_Sender a ON daily.id = a.assoc_id

	 and a.sign is null 
	 and uassoc.user_id  =#{beSentPeopleId} 
	 and daily.id = #{id} 
	 and daily.com_content = #{comContent} 
	 and daily.uncom_content = #{uncomContent} 
	 and daily.need_concert_content = #{needConcertContent} 
	 and daily.wait_contet = #{waitContet} 
	 and daily.must_contet = #{mustContet} 
	 AND employee.name  like CONCAT ('%',#{userName},'%') 
	 and daily.create_time = #{createTime} 
	 and  DATE_FORMAT(daily.create_time,'%Y-%m-%d') >=  DATE_FORMAT(#{startTime},'%Y-%m-%d')
	  and  DATE_FORMAT(daily.create_time,'%Y-%m-%d') <=   DATE_FORMAT(#{endTime},'%Y-%m-%d')
	 and daily.update_by = #{updateBy} 
	 and daily.update_time = #{updateTime} 
	 and daily.del_flag = #{delFlag} 
	 and dept.name = #{deptName} 
	 and dept.dept_id = #{deptId} 
	 and dept.id_path like CONCAT (#{idPath},'%') 
	 and daily.status = #{status} 
	 and daily.create_by = #{createBy} 



		order by ${sort} ${order}


		order by daily.create_time DESC ,daily.status desc ,sign asc



	limit #{offset}, #{limit}

