FROM
cus_daily_report daily
LEFT JOIN sys_user employee ON employee.user_id = daily.create_by
LEFT JOIN cus_dictionary b ON b.id = daily.status
LEFT JOIN sys_dept dept ON dept.dept_id = employee.dept_id
 LEFT JOIN cus_user_assoc uassoc on uassoc.assoc_id = daily.id and uassoc.assoc_type = 'DAILY_REPORT_TARGET'
LEFT JOIN view_daliyReport_Sender a ON daily.id = a.assoc_id

	 and a.sign is null 
	 and uassoc.user_id  =#{beSentPeopleId} 
	 and daily.id = #{id} 
	 and daily.com_content = #{comContent} 
	 and daily.uncom_content = #{uncomContent} 
	 and daily.need_concert_content = #{needConcertContent} 
	 and daily.wait_contet = #{waitContet} 
	 and daily.must_contet = #{mustContet} 
	 AND employee.name  like CONCAT ('%',#{userName},'%') 
	 and daily.create_time = #{createTime} 
	 and  DATE_FORMAT(daily.create_time,'%Y-%m-%d') >=  DATE_FORMAT(#{startTime},'%Y-%m-%d')
	  and  DATE_FORMAT(daily.create_time,'%Y-%m-%d') <=   DATE_FORMAT(#{endTime},'%Y-%m-%d')
	 and daily.update_by = #{updateBy} 
	 and daily.update_time = #{updateTime} 
	 and daily.del_flag = #{delFlag} 
	 and dept.name = #{deptName} 
	 and dept.dept_id = #{deptId} 
	 and dept.id_path like CONCAT (#{idPath},'%') 
