mapper.select:
		select `id`,`insurancesort`,`sortid`,`amount`,`charge`,`starttime`,`endtime`,`insurcompany`,`companyid`,`settlementdate`,`settleamount`,`insursalesman`,`agent`,`agentid`,`deviceid`,`lossoccurred`,`create_by`,`create_time`,`update_by`,`update_time`,`del_flag` from cus_device_insurance

		  		   and id = #{id} 
		  		   and insurancesort = #{insurancesort} 
		  		   and sortid = #{sortid} 
		  		   and amount = #{amount} 
		  		   and charge = #{charge} 
		  		   and starttime = #{starttime} 
		  		   and endtime = #{endtime} 
		  		   and insurcompany = #{insurcompany} 
		  		   and companyid = #{companyid} 
		  		   and settlementdate = #{settlementdate} 
		  		   and settleamount = #{settleamount} 
		  		   and insursalesman = #{insursalesman} 
		  		   and agent = #{agent} 
		  		   and agentid = #{agentid} 
		  		   and deviceid = #{deviceid} 
			     and lossoccurred = #{lossoccurred} 
				 and create_by = #{createBy} 
				 and create_time = #{createTime} 
				 and update_by = #{updateBy} 
				 and update_time = #{updateTime} 
				 and del_flag = #{delFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select
a.id,
a.insurancesort,
cd.name as insurancesort_name,
a.insurcompany,
a.agent as agentName,
a.settleamount,
a.amount,
a.charge,
a.starttime,
a.endtime,
a.settlementdate,
a.insursalesman,
a.settleamount,
b.name as agent_name,
c.name as  company_name
from cus_device_insurance a
LEFT JOIN cus_dictionary cd ON a.sortid=cd.id
LEFT JOIN cus_supplier c ON a.companyid=c.id
LEFT JOIN sys_user b ON a.agentid=b.user_id

 and a.id = #{id} 
 and a.insurancesort = #{insurancesort} 
 and a.sortid = #{sortid} 
 and a.amount = #{amount} 
 and a.charge = #{charge} 
 and a.starttime = #{starttime} 
 and a.endtime = #{endtime} 
 and a.insurcompany = #{insurcompany} 
 and a.companyid = #{companyid} 
 and a.settlementdate = #{settlementdate} 
 and a.settleamount = #{settleamount} 
 and a.insursalesman = #{insursalesman} 
 and a.agent = #{agent} 
 and a.agentid = #{agentid} 
 and a.deviceid = #{deviceid} 
 and a.lossoccurred = #{lossoccurred} 
 and a.create_by = #{createBy} 
 and a.create_time = #{createTime} 
 and a.update_by = #{updateBy} 
 and a.update_time = #{updateTime} 
 and a.del_flag = #{delFlag} 



	order by ${sort} ${order}


	order by id desc



limit #{offset}, #{limit}

select count(*)
from cus_device_insurance a
LEFT JOIN cus_dictionary cd ON a.sortid=cd.id
LEFT JOIN cus_supplier c ON a.companyid=c.id
LEFT JOIN sys_user b ON a.agentid=b.user_id

	  and a.id = #{id} 
	  and a.insurancesort = #{insurancesort} 
	  and a.sortid = #{sortid} 
	  and a.amount = #{amount} 
	  and a.charge = #{charge} 
	  and a.starttime = #{starttime} 
	  and a.endtime = #{endtime} 
	  and a.insurcompany = #{insurcompany} 
	  and a.companyid = #{companyid} 
	  and a.settlementdate = #{settlementdate} 
	  and a.settleamount = #{settleamount} 
	  and a.insursalesman = #{insursalesman} 
	  and a.agent = #{agent} 
	  and a.agentid = #{agentid} 
	  and a.deviceid = #{deviceid} 
	  and a.lossoccurred = #{lossoccurred} 
	  and a.create_by = #{createBy} 
	  and a.create_time = #{createTime} 
	  and a.update_by = #{updateBy} 
