mapper.select:
		select `id`,`device_id`,`spare_id`,`amount`,`spare_locationid`,`def1`,`def2`,`def3`,`def4`,`def5`,`create_by`,`create_time`,`update_by`,`update_time`,`del_flag` from cus_device_spare

		  		   and id = #{id} 
		  		   and device_id = #{deviceId} 
		  		   and spare_id = #{spareId} 
		  		   and amount = #{amount} 
		  		   and spare_locationid = #{spareLocationid} 
		  		   and def1 = #{def1} 
		  		   and def2 = #{def2} 
		  		   and def3 = #{def3} 
		  		   and def4 = #{def4} 
		  		   and def5 = #{def5} 
			 and create_by = #{createBy} 
			 and create_time = #{createTime} 
			 and update_by = #{updateBy} 
			 and update_time = #{updateTime} 
			 and del_flag = #{delFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from cus_device_spare

  		   and id = #{id} 
  		   and device_id = #{deviceId} 
  		   and spare_id = #{spareId} 
  		   and amount = #{amount} 
  		   and spare_locationid = #{spareLocationid} 
  		   and def1 = #{def1} 
  		   and def2 = #{def2} 
  		   and def3 = #{def3} 
  		   and def4 = #{def4} 
  		   and def5 = #{def5} 
	  and create_by = #{createBy} 
	  and create_time = #{createTime} 
	  and update_by = #{updateBy} 
	  and update_time = #{updateTime} 
	  and del_flag = #{delFlag}

	select
         csp.id,
         csp.serial_no as code,
         csp.name,
         csp.specification as type,
         a.name as spartTypeName,
         cd.name as loacationName
		 from cus_materiel csp
		left JOIN cus_device_spare cds on cds.spare_id=csp.id
		left JOIN cus_facility_location cd on csp.default_location=cd.id
    	left JOIN cus_dictionary a on csp.type=a.id

			 and cds.id = #{id} 
			 and cds.device_id = #{deviceId} 
			 and cds.spare_id = #{spareId} 
			 and cds.amount = #{amount} 
			 and cds.spare_locationid = #{spareLocationid} 
			 and cds.create_by = #{createBy} 
			 and cds.create_time = #{createTime} 
			 and cds.update_by = #{updateBy} 
			 and cds.update_time = #{updateTime} 
			 and cds.del_flag = #{delFlag} 



				order by ${sort} ${order}


				order by id desc



			limit #{offset}, #{limit}

select
count(*)
from cus_materiel csp
left JOIN cus_device_spare cds on cds.spare_id=csp.id
left JOIN cus_facility_location cd on csp.default_location=cd.id
left JOIN cus_dictionary a on csp.type=a.id

	 and cds.id = #{id} 
	 and cds.device_id = #{deviceId} 
	 and cds.spare_id = #{spareId} 
	 and cds.amount = #{amount} 
	 and cds.spare_locationid = #{spareLocationid} 
	 and cds.create_by = #{createBy} 
	 and cds.create_time = #{createTime} 
	 and cds.update_by = #{updateBy} 
	 and cds.update_time = #{updateTime} 
	 and cds.del_flag = #{delFlag}
