mapper.select:
select `id`,`name`,`value`,`parent_id`,`type_id`,`create_by`,`create_time`,`update_by`,`update_time`,`del_flag` from cus_dictionary

	 and id = #{id} 
	 and name = #{name} 
	 and value = #{value} 
	 and parent_id = #{parentId} 
	 and type_id = #{typeId} 
	 and create_by = #{createBy} 
	 and create_time = #{createTime} 
	 and update_by = #{updateBy} 
	 and update_time = #{updateTime} 
	 and del_flag = #{delFlag} 



		order by ${sort} ${order}


		order by id desc



	limit #{offset}, #{limit}

select count(1) from cus_dictionary

	 and id = #{id} 
	 and name = #{name} 
	 and value = #{value} 
	 and parent_id = #{parentId} 
	 and type_id = #{typeId} 
	 and create_by = #{createBy} 
	 and create_time = #{createTime} 
	 and update_by = #{updateBy} 
	 and update_time = #{updateTime} 
	 and del_flag = #{delFlag}

SELECT
dic.id,
dic.name,
dic.value,
dic.parent_id,
dic.type_id,
dic.create_by,
dic.create_time,
dic.update_by,
dic.update_time,
dic.del_flag,
dictype.code as typecode,
dictype.name as typename
FROM
cus_dictionary dic
LEFT JOIN cus_dictionary_type dictype ON dic.type_id = dictype.id

	 and dic.id = #{id} 
	 and dic.name = #{name} 
	 and dic.value = #{value} 
	 and dic.parent_id = #{parentId} 
	 and dic.type_id = #{typeId} 
	 and dic.create_by = #{createBy} 
	 and dic.name LIKE CONCAT('%',CONCAT(#{content},'%')) 
	 and dic.create_time = #{createTime} 
	 and dic.update_by = #{updateBy} 
	 and dic.update_time = #{updateTime} 
	 and dic.del_flag = #{delFlag} 



		order by ${sort} ${order}


		order by id desc



	limit #{offset}, #{limit}

SELECT
count(1)
FROM
cus_dictionary dic
LEFT JOIN cus_dictionary_type dictype ON dic.type_id = dictype.id

	 and dic.id = #{id} 
	 and dic.name = #{name} 
	 and dic.value = #{value} 
	 and dic.parent_id = #{parentId} 
	 and dic.type_id = #{typeId} 
	 and dic.create_by = #{createBy} 
	 and dic.name LIKE CONCAT('%',CONCAT(#{content},'%')) 
	 and dic.create_time = #{createTime} 
	 and dic.update_by = #{updateBy} 
	 and dic.update_time = #{updateTime} 
	 and dic.del_flag = #{delFlag}

SELECT dic.id, dic.name, dic.value, dic.parent_id, dic.type_id, dic.create_by, dic.create_time, dic.update_by, dic.update_time, dic.del_flag FROM cus_dictionary dic LEFT JOIN cus_dictionary_type dictype ON dic.type_id = dictype.id
and dictype.code = #{type}
order by id asc
