mapper.select:
		select `id`,`code`,`name`,`parent_id`,`is_update`,`create_by`,`create_time`,`update_by`,`update_time`,`del_flag` from cus_dictionary_type

		  		   and id = #{id} 
		  		   and code = #{code} 
		  		   and name = #{name} 
		  		   and parent_id = #{parentId} 
		  		   and create_by = #{createBy} 
		  		   and create_time = #{createTime} 
		  		   and update_by = #{updateBy} 
		  		   and update_time = #{updateTime} 
		  		   and del_flag = #{delFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from cus_dictionary_type

  		   and id = #{id} 
  		   and code = #{code} 
  		   and name = #{name} 
  		   and parent_id = #{parentId} 
  		   and create_by = #{createBy} 
  		   and create_time = #{createTime} 
  		   and update_by = #{updateBy} 
  		   and update_time = #{updateTime} 
  		   and del_flag = #{delFlag}

		select id as id,code as code,name as name,parent_id as parentid,is_update AS isUpdate,create_by as createBy,create_time as createTime,update_by as updateBy,update_time as updateTime,del_flag as delFlag from cus_dictionary_type

			 and id = #{id} 
			 and code = #{code} 
			 and name = #{name} 
             and name LIKE #{fuzzySearch} 
			 and is_update = #{isUpdate} 
			 and parent_id = #{parentId} 
			 and is_update = #{isUpdate} 
			 and create_by = #{createBy} 
			 and create_time = #{createTime} 
			 and update_by = #{updateBy} 
			 and update_time = #{updateTime} 
			 and del_flag = #{delFlag} 



				order by ${sort} ${order}


				order by id desc



			limit #{offset}, #{limit}

		select count(*) from cus_dictionary_type

			 and id = #{id} 
			 and code = #{code} 
			 and name = #{name} 
             and name LIKE #{fuzzySearch} 
			 and is_update = #{isUpdate} 
			 and parent_id = #{parentId} 
			 and is_update = #{isUpdate} 
			 and create_by = #{createBy} 
			 and create_time = #{createTime} 
			 and update_by = #{updateBy} 
			 and update_time = #{updateTime} 
			 and del_flag = #{delFlag}
