mapper.select:
		select `id`,`name`,`type`,`up_user`,`device_id`,`source`,`up_time`,`version`,`description`,`create_by`,`create_time`,`update_by`,`update_time`,`del_flag` from cus_document

		  		   and id = #{id} 
		  		   and name = #{name} 
		  		   and type = #{type} 
		  		   and up_user = #{upUser} 
		  		   and device_id = #{deviceId} 
		  		   and source = #{source} 
		  		   and up_time = #{upTime} 
		  		   and version = #{version} 
		  		   and description = #{description} 
		  		   and create_by = #{createBy} 
		  		   and create_time = #{createTime} 
		  		   and update_by = #{updateBy} 
		  		   and update_time = #{updateTime} 
		  		   and del_flag = #{delFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from cus_document

  		   and id = #{id} 
  		   and name = #{name} 
  		   and type = #{type} 
  		   and up_user = #{upUser} 
  		   and device_id = #{deviceId} 
  		   and source = #{source} 
  		   and up_time = #{upTime} 
  		   and version = #{version} 
  		   and description = #{description} 
  		   and create_by = #{createBy} 
  		   and create_time = #{createTime} 
  		   and update_by = #{updateBy} 
  		   and update_time = #{updateTime} 
  		   and del_flag = #{delFlag}

SELECT
document.id,
document.name,
document.type,
document.up_user,
document.device_id,
document.source,
document.up_time,
document.version,
document.description,
document.create_by,
document.create_time,
document.update_by,
document.update_time,
document.del_flag,
typedic.name as typename,
sourcedic.name as sourcename,
upuser.name as upname
FROM
cus_document document
LEFT JOIN cus_dictionary typedic ON typedic.id = document.type
LEFT JOIN cus_dictionary sourcedic on sourcedic.id = document.source
LEFT JOIN sys_user upuser on upuser.user_id = document.up_user

	 and document.id = #{id} 
	 and document.name = #{name} 
	 and document.type = #{type} 
	 and document.up_user = #{upUser} 
	 and document.device_id = #{deviceId} 
	 and document.source = #{source} 
	 and document.up_time = #{upTime} 
	 and document.version = #{version} 
	 and document.description = #{description} 
	 and document.create_by = #{createBy} 
	 and document.create_time = #{createTime} 
	 and document.update_by = #{updateBy} 
	 and document.update_time = #{updateTime} 
	 and document.del_flag = #{delFlag} 
	 and upuser.name = #{upUserName} 



		order by ${sort} ${order}


		order by id desc



	limit #{offset}, #{limit}

SELECT
count(1)
FROM
cus_document document
LEFT JOIN cus_dictionary typedic ON typedic.id = document.type
LEFT JOIN cus_dictionary sourcedic on sourcedic.id = document.source
LEFT JOIN sys_user upuser on upuser.user_id = document.up_user

	 and document.id = #{id} 
	 and document.name = #{name} 
	 and document.type = #{type} 
	 and document.up_user = #{upUser} 
	 and document.device_id = #{deviceId} 
	 and document.source = #{source} 
	 and document.up_time = #{upTime} 
	 and document.version = #{version} 
	 and document.description = #{description} 
	 and document.create_by = #{createBy} 
	 and document.create_time = #{createTime} 
	 and document.update_by = #{updateBy} 
	 and document.update_time = #{updateTime} 
	 and document.del_flag = #{delFlag} 
	 and upuser.name = #{upUserName}
