mapper.select:
		SELECT 

		FROM cus_expense



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

sql.where:
							 and LEFT(code,10) = #{maxNo} 
							 and id = #{id} 
                             and code = #{code} 
                             and type = #{type} 
                             and name = #{name} 
                             and auditor = #{auditor} 
                             and audit_sign = #{auditSign} 
                             and remarks = #{remarks} 
                             and create_by = #{createBy} 
                             and create_time = #{createTime} 
                             and update_by = #{updateBy} 
                             and update_time = #{updateTime} 
                             and del_flag = #{delFlag} 
							and del_flag=0

mapper.select:
select a.id, a.code, a.type, a.name, b.name as typeName, a.remarks, a.audit_sign as auditSign, c.name as auditSignName, a.auditor, d.name as auditorName, a.create_by as createBy, e.name as createByName, a.create_time as createTime from cus_expense a LEFT JOIN cus_dictionary b on a.type=b.id LEFT JOIN cus_dictionary c on a.audit_sign=c.id LEFT JOIN sys_user d on a.auditor=d.user_id LEFT JOIN sys_user e on a.create_by=e.user_id
and a.name like CONCAT('%',#{name},'%')
and a.audit_sign =#{auditSign}
and a.id = #{id} and a.del_flag=0
order by ${sort} ${order}
order by id desc
limit #{offset}, #{limit}
