mapper.select:
		select `id`,`serial_no`,`name`,`description`,`facility_id`,`create_by`,`create_time`,`update_by`,`update_time`,`del_flag`,audit_sign,auditor from cus_facility_location

		  		   and id = #{id} 
             and LEFT(serial_no,CHAR_LENGTH(#{maxNo})) = #{maxNo} 
		  		   and serial_no = #{serialNo} 
		  		   and name = #{name} 
		  		   and description = #{description} 
		  		   and facility_id = #{facilityId} 
		  		   and create_by = #{createBy} 
		  		   and create_time = #{createTime} 
		  		   and update_by = #{updateBy} 
		  		   and update_time = #{updateTime} 
		  		   and del_flag = #{delFlag} 
             and audit_sign = #{auditSign} 
             and auditor = #{auditor} 
			 and facility_id IN


						#{item}






                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

		SELECT
		facl.id as id,
		facl.serial_no as serialNo,
		facl.name as name,
		facl.description as description,
		fac.name as facilityName,
		fac.serial_no AS facilitySerialNo,
		facl.create_by as createBy,
		facl.create_time as createTime,
		facl.update_by as updateBy,
		facl.update_time as updateTime,
		facl.del_flag as delFlag,
        facl.remarks AS remarks,
        facl.facility_id as facilityId,
        sys.`name` AS auditorName,
        facl.audit_sign AS auditSign,
        dica.`name` AS auditSignName
		FROM
		cus_facility_location facl
		LEFT JOIN cus_facility fac ON fac.id = facl.facility_id
        LEFT JOIN sys_user sys ON sys.user_id = facl.auditor
        LEFT JOIN cus_dictionary dica on dica.id=facl.audit_sign

			 and facl.id = #{id} 
			 and facl.serial_no = #{serialNo} 
			 and  CONCAT(facl.name,facl.serial_no) LIKE CONCAT('%',#{name},'%') 
			 and facl.description = #{description} 
			 and facl.facility_id = #{facilityId} 
			 and facl.create_by = #{createBy} 
			 and facl.facl.create_time = #{createTime} 
			 and facl.update_by = #{updateBy} 
			 and facl.update_time = #{updateTime} 
			 and facl.del_flag = #{delFlag} 
             and facl.audit_sign = #{auditSign} 
             and facl.auditor = #{auditor} 
			AND facl.del_flag = 0



				order by ${sort} ${order}


				order by facl.id desc



			limit #{offset}, #{limit}

		select count(*) from cus_facility_location

		  		   and id = #{id} 
		  		   and serial_no = #{serialNo} 
		  		   and  CONCAT(name,serial_no) LIKE CONCAT('%',#{name},'%') 
			  and name = #{nameSearch} 
		  		   and description = #{description} 
		  		   and facility_id = #{facilityId} 
		  		   and create_by = #{createBy} 
		  		   and create_time = #{createTime} 
		  		   and update_by = #{updateBy} 
		  		   and update_time = #{updateTime} 
		  		   and del_flag = #{delFlag} 
              and audit_sign = #{auditSign} 
              and auditor = #{auditor} 
		  		  AND del_flag = 0

mapper.update:
update cus_facility_location
`serial_no` = #{serialNo}, `name` = #{name}, `description` = #{description}, `facility_id` = #{facilityId}, `create_by` = #{createBy}, `create_time` = #{createTime}, `update_by` = #{updateBy}, `update_time` = #{updateTime}, `del_flag` = #{delFlag}, auditor = #{auditor}, audit_sign = #{auditSign}, remarks = #{remarks}
where id = #{id}
