mapper.select:
		select
		`id`,`serial_no`,`name`,`description`,`create_by`,`create_time`,`update_by`,
		`update_time`,`del_flag`,`facility_type`,audit_sign,auditor
		from cus_facility

		  		   and id = #{id} 
		  		   and serial_no = #{serialNo} 
		  		   and name = #{name} 
		  		   and description = #{description} 
		  		   and create_by = #{createBy} 
		  		   and create_time = #{createTime} 
		  		   and update_by = #{updateBy} 
		  		   and update_time = #{updateTime} 
		  		   and del_flag = #{delFlag} 
		  		   and facility_type = #{facilityType} 
             and audit_sign = #{auditSign} 
             and auditor = #{auditor} 
			 and `name` IN


						#{item}






                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

        SELECT
        fac.id AS id,
        fac.serial_no AS serialNo,
        fac.name AS name,
        fac.description AS description,
        fac.create_by AS createBy,
        fac.create_time AS createTime,
        fac.update_by AS updateBy,
        fac.update_time AS updateTime,
        fac.del_flag AS delFlag,
        dic. id AS facilityType,
        dic. name AS facilityTypeName,
        fac.`audit_sign` AS auditSign,
        auditor.`name` AS auditorName,
        dica.name AS auditSignName
        FROM
        cus_facility fac
        LEFT JOIN cus_dictionary dic ON dic.id = fac.facility_type
        LEFT JOIN cus_dictionary dica ON dica.id = fac.audit_sign
        LEFT JOIN sys_user auditor ON auditor.user_id = fac.auditor

			 and fac.id = #{id} 
			 and fac.serial_no = #{serialNo} 
			 and fac.name LIKE #{name} 
			 and fac.description = #{description} 
			 and fac.create_by = #{createBy} 
			 and fac.create_time = #{createTime} 
			 and fac.update_by = #{updateBy} 
			 and fac.update_time = #{updateTime} 
			 and fac.del_flag = #{delFlag} 
			 and fac.facility_type = #{facilityType} 
             and fac.audit_sign = #{auditSign} 
             and fac.auditor = #{auditor} 
			AND fac.del_flag = 0



				order by ${sort} ${order}


				order by id desc



			limit #{offset}, #{limit}

select count(*) from cus_facility

     and id = #{id} 
     and serial_no = #{serialNo} 
     and name LIKE #{name} 
     and description = #{description} 
     and create_by = #{createBy} 
     and create_time = #{createTime} 
     and update_by = #{updateBy} 
     and update_time = #{updateTime} 
     and del_flag = #{delFlag} 
     and facility_type = #{facilityType} 
     and audit_sign = #{auditSign} 
     and auditor = #{auditor} 
    AND del_flag = 0

		select count(*) from cus_facility

		  		   and id = #{id} 
