              and auditor = #{auditor} 
		  			AND del_flag = 0

mapper.update:
update cus_facility
`serial_no` = #{serialNo}, `name` = #{name}, `description` = #{description}, `create_by` = #{createBy}, `create_time` = #{createTime}, `update_by` = #{updateBy}, `update_time` = #{updateTime}, `del_flag` = #{delFlag}, `facility_type` = #{facilityType}, auditor = #{auditor}, audit_sign = #{auditSign}
where id = #{id}
