mapper.select:
		select `id`,`device_type`,`device_model`,`fr_no`,`fr_appearence`,`fr_type`,`fr_reason`,`deal_method`,`description`,`up_user`,`up_time`,`create_by`,`create_time`,`update_by`,`update_time`,`del_flag` from cus_frdb

		  		   and id = #{id} 
		  		   and device_type = #{deviceType} 
		  		   and device_model = #{deviceModel} 
		  		   and fr_no = #{frNo} 
		  		   and fr_appearence = #{frAppearence} 
		  		   and fr_type = #{frType} 
		  		   and fr_reason = #{frReason} 
		  		   and deal_method = #{dealMethod} 
		  		   and description = #{description} 
		  		   and up_user = #{upUser} 
		  		   and up_time = #{upTime} 
		  		   and create_by = #{createBy} 
		  		   and create_time = #{createTime} 
		  		   and update_by = #{updateBy} 
		  		   and update_time = #{updateTime} 
		  		   and del_flag = #{delFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from cus_frdb

  		   and id = #{id} 
  		   and device_type = #{deviceType} 
  		   and device_model = #{deviceModel} 
  		   and fr_no = #{frNo} 
  		   and fr_appearence = #{frAppearence} 
  		   and fr_type = #{frType} 
  		   and fr_reason = #{frReason} 
  		   and deal_method = #{dealMethod} 
  		   and description = #{description} 
  		   and up_user = #{upUser} 
  		   and up_time = #{upTime} 
  		   and create_by = #{createBy} 
  		   and create_time = #{createTime} 
  		   and update_by = #{updateBy} 
  		   and update_time = #{updateTime} 
  		   and del_flag = #{delFlag}

SELECT
frdb.id,
frdb.device_type,
frdb.device_model,
frdb.fr_no,
frdb.fr_appearence,
frdb.fr_type,
frdb.fr_reason,
frdb.deal_method,
frdb.description,
frdb.up_user,
frdb.up_time,
frdb.create_by,
frdb.create_time,
frdb.update_by,
frdb.update_time,
frdb.del_flag,
devicetype.name as devicetypename,
frtype.name as frtypename,
upuser.name as upusername
FROM
cus_frdb frdb
LEFT JOIN cus_dictionary devicetype on devicetype.id = frdb.device_type
LEFT JOIN cus_dictionary frtype on frtype.id = frdb.fr_type
LEFT JOIN sys_user upuser ON upuser.user_id = frdb.up_user

	 and frdb.id = #{id} 
	 and frdb.device_type = #{deviceType} 
	 and frdb.device_model = #{deviceModel} 
	 and frdb.fr_no = #{frNo} 
	 and frdb.fr_appearence = #{frAppearence} 
	 and frdb.fr_type = #{frType} 
	 and frdb.fr_reason = #{frReason} 
	 and frdb.deal_method = #{dealMethod} 
	 and frdb.description = #{description} 
	 and frdb.up_user = #{upUser} 
	 and frdb.up_time = #{upTime} 
	 and frdb.create_by = #{createBy} 
	 and frdb.create_time = #{createTime} 
	 and frdb.update_by = #{updateBy} 
	 and frdb.update_time = #{updateTime} 
	 and frdb.del_flag = #{delFlag} 
	 and upuser.name = #{upUserName} 



		order by ${sort} ${order}


		order by id desc



	limit #{offset}, #{limit}

SELECT
count(1)
FROM
cus_frdb frdb
LEFT JOIN cus_dictionary devicetype on devicetype.id = frdb.device_type
LEFT JOIN cus_dictionary frtype on frtype.id = frdb.fr_type
LEFT JOIN sys_user upuser ON upuser.user_id = frdb.up_user

	 and frdb.id = #{id} 
	 and frdb.device_type = #{deviceType} 
	 and frdb.device_model = #{deviceModel} 
	 and frdb.fr_no = #{frNo} 
	 and frdb.fr_appearence = #{frAppearence} 
	 and frdb.fr_type = #{frType} 
	 and frdb.fr_reason = #{frReason} 
	 and frdb.deal_method = #{dealMethod} 
	 and frdb.description = #{description} 
	 and frdb.up_user = #{upUser} 
	 and frdb.up_time = #{upTime} 
	 and frdb.create_by = #{createBy} 
	 and frdb.create_time = #{createTime} 
	 and frdb.update_by = #{updateBy} 
	 and frdb.update_time = #{updateTime} 
	 and frdb.del_flag = #{delFlag} 
	 and upuser.name = #{upUserName}
