mapper.select:
		select 
		`id`,
		`code`,
		`name`,
		`parent_id`,
		`is_system`
		from cus_materiel_type

		  		   and id = #{id} 
			       and `name` LIKE #{nameSearch}
			       and `name` = #{name} 
			       and `code` = #{code} 
		  		   and parent_id = #{parentId} 
		  		   and create_by = #{createBy} 
		  		   and create_time = #{createTime} 
		  		   and update_by = #{updateBy} 
		  		   and update_time = #{updateTime} 
		  		   and del_flag = #{delFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from cus_materiel_type

  		   and id = #{id} 
	       and `name` LIKE #{nameSearch}
	       and `name` = #{name} 
	       and `code` = #{code} 
  		   and parent_id = #{parentId} 
  		   and create_by = #{createBy} 
  		   and create_time = #{createTime} 
  		   and update_by = #{updateBy} 
  		   and update_time = #{updateTime} 
  		   and del_flag = #{delFlag}

select count(*)
FROM
cus_materiel_type

	 AND `id` <> #{id} AND
	(
	`name` = #{name}
	OR `code` = #{code}
	)

select count(*) FROM cus_materiel_type mtype INNER JOIN cus_materiel m ON m.`type`=mtype.`id` WHERE mtype.`id` IN
#{id}
AND m.del_flag <>1

        select
        `id`,
        `name`,
        `parent_id` AS parentId,
		`is_system` AS isSystem
        from cus_materiel_type

             and id = #{id} 
             and `name` LIKE #{nameSearch}
			 and `name` = #{name} 
			 and `code` = #{code} 
             and parent_id = #{parentId} 
             and create_by = #{createBy} 
             and create_time = #{createTime} 
             and update_by = #{updateBy} 
             and update_time = #{updateTime} 
             and del_flag = #{delFlag} 



                order by ${sort} ${order}


                order by id desc



            limit #{offset}, #{limit}
