mapper.select:
SELECT

FROM cus_month_report



		order by ${sort} ${order}


		order by id desc



	limit #{offset}, #{limit}

SELECT

	case when uassoc.sign=1 then 3
	when uassoc.sign is null then 1
	end sign,
	case when uassoc.sign=1 then '已读'
	when uassoc.sign is null then '未读'
	end readStatus,
	uassoc.user_id as userId,

month.id as id,
month.com_content as comContent,
month.uncom_content as uncomContent,
month.need_concert_content as needConcertContent,
month.wait_contet as waitContet,
month.must_contet as mustContet,
month.create_by as createBy,
month.create_time as createTime,
month.update_by as updateBy,
month.update_time as updateTime,
month.del_flag as delFlag,
dept.dept_id as deptId,
dept.id_path as idPath,
dept.name as deptName,
month.status,
b.name as statusName,
employee.name as userName
FROM
cus_month_report month
LEFT JOIN sys_user employee ON employee.user_id = month.create_by
LEFT JOIN cus_dictionary b ON b.id = month.status
LEFT JOIN sys_dept dept ON dept.dept_id = employee.dept_id
LEFT JOIN cus_user_assoc uassoc on uassoc.assoc_id = month.id and uassoc.assoc_type = 'MONTH_REPORT_TARGET' 
LEFT JOIN view_month_approve_mens a ON month.id = a.assoc_id

	 and uassoc.user_id  =#{beSentPeopleId} 
	 and a.sign is null 
	 and month.id = #{id} 
	 and month.com_content = #{comContent} 
	 and month.uncom_content = #{uncomContent} 
	 and month.need_concert_content = #{needConcertContent} 
	 and month.wait_contet = #{waitContet} 
	 and month.must_contet = #{mustContet} 
	 and month.update_by = #{updateBy} 
	 and month.update_time = #{updateTime} 
	 and month.del_flag = #{delFlag} 
	 and dept.name = #{deptName} 
	 and dept.dept_id = #{deptId} 
	 and dept.id_path like CONCAT (#{idPath},'%') 
	 and  DATE_FORMAT(month.create_time,'%Y-%m-%d') >=  DATE_FORMAT(#{startTime},'%Y-%m-%d')
	  and  DATE_FORMAT(month.create_time,'%Y-%m-%d') <=   DATE_FORMAT(#{endTime},'%Y-%m-%d')
	 AND employee.name  like CONCAT ('%',#{userName},'%') 
	 and month.status = #{status} 
	 and month.create_time = #{createTime} 
	 and month.create_by = #{createBy} 



		order by ${sort} ${order}


		order by  month.status ASC ,month.create_time DESC



	limit #{offset}, #{limit}

SELECT
count(1)
FROM
cus_month_report month
LEFT JOIN sys_user employee ON employee.user_id = month.create_by
LEFT JOIN cus_dictionary b ON b.id = month.status
LEFT JOIN sys_dept dept ON dept.dept_id = employee.dept_id
LEFT JOIN cus_user_assoc uassoc on uassoc.assoc_id = month.id and uassoc.assoc_type = 'MONTH_REPORT_TARGET' 
LEFT JOIN view_month_approve_mens a ON month.id = a.assoc_id

	 and uassoc.user_id  =#{beSentPeopleId} 
	 and a.sign is null 
	 and month.id = #{id} 
	 and month.com_content = #{comContent} 
	 and month.uncom_content = #{uncomContent} 
	 and month.need_concert_content = #{needConcertContent} 
	 and month.wait_contet = #{waitContet} 
	 and month.must_contet = #{mustContet} 
	 and month.update_by = #{updateBy} 
	 and month.update_time = #{updateTime} 
	 and month.del_flag = #{delFlag} 
	 and dept.name = #{deptName} 
	 and dept.dept_id = #{deptId} 
	 and dept.id_path like CONCAT (#{idPath},'%') 
	 and  DATE_FORMAT(month.create_time,'%Y-%m-%d') >=  DATE_FORMAT(#{startTime},'%Y-%m-%d')
	  and  DATE_FORMAT(month.create_time,'%Y-%m-%d') <=   DATE_FORMAT(#{endTime},'%Y-%m-%d')
	 AND employee.name  like CONCAT ('%',#{userName},'%') 
	 and month.status = #{status} 
	 and month.create_time = #{createTime} 
	 and month.create_by = #{createBy}

select `id`, `status` from cus_month_report
and id in
#{item}
and status =#{status}
