mapper.select:
		SELECT  
		patrolrecord.id as recordId,
		patrolrecord.`work_orderNo` as workOrderNo,
		patrolrecord.`name` AS recordName,
		dict.`value` as `statusValue`,
		dict.`name` as `statusName`,
		patrolrecord.`complete_time`
		FROM cus_patrol_detail patroldetail
		LEFT JOIN  cus_patrol_record patrolrecord ON patrolrecord.`id`=patroldetail.`record_id`
		LEFT JOIN  cus_device device ON device.`id`=patroldetail.`device_id`
		LEFT JOIN cus_dictionary dict ON dict.id=patrolrecord.`status`

		  		   and patroldetail.id = #{id} 
		  		   and patroldetail.`device_id` = #{deviceId} 
		  		   and patroldetail.record_id = #{recordId} 
				   and patroldetail.create_by = #{createBy} 
				   and patroldetail.create_time = #{createTime} 
				   and patroldetail.update_by = #{updateBy} 
				   and patroldetail.update_time = #{updateTime} 
				    and  DATE_FORMAT(patrolrecord.`complete_time`,'%Y-%m-%d') >=  DATE_FORMAT(#{startTime},'%Y-%m-%d')
				    and  DATE_FORMAT(patrolrecord.`complete_time`,'%Y-%m-%d') <=  DATE_FORMAT(#{endTime},'%Y-%m-%d')
				   and patroldetail.del_flag = #{delFlag} 						  		   

		GROUP BY patrolrecord.id


                order by ${sort} ${order}


                order by patrolrecord.id desc



			limit #{offset}, #{limit}

		select `id`,`device_id`,`project_id`,`result`,`status`,`off_time`,`content`,`record_id`,`create_by`,`create_time`,`update_by`,`update_time`,`del_flag` from cus_patrol_detail

		  		   and id = #{id} 
		  		   and device_id = #{deviceId} 
		  		   and project_id = #{projectId} 
		  		   and result = #{result} 
		  		   and status = #{status} 
		  		   and content = #{content} 
		  		   and record_id = #{recordId} 
				   and create_by = #{createBy} 
				   and create_time = #{createTime} 
				   and update_by = #{updateBy} 
				   and update_time = #{updateTime} 
				   and del_flag = #{delFlag} 						  		   



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

		SELECT
		device.id as deviceId,
		patrolrecord.`id` as recordId,
		patrolproject.`name` as projectName,
		patrolproject.`function` as projectFunction,
		patroldetail.`result` as projectResult,
		patroldetail.`record_id` as recordId
		FROM cus_patrol_detail patroldetail
		LEFT JOIN  cus_patrol_project patrolproject ON patrolproject.`id`=patroldetail.`project_id`
		LEFT JOIN  cus_patrol_record patrolrecord ON patrolrecord.`id`=patroldetail.`record_id`
		LEFT JOIN  cus_device device ON device.`id`=patroldetail.`device_id`

		  		   and patroldetail.id = #{id} 
		  		   and patroldetail.device_id = #{deviceId} 
		  		   and patroldetail.project_id = #{projectId} 
		  		   and patroldetail.result = #{result} 
		  		   and patroldetail.status = #{status} 
		  		   and patroldetail.content = #{content} 
		  		   and patroldetail.record_id = #{recordId} 



                ORDER BY ${sort} ${order}


                ORDER BY patroldetail.id desc



			limit #{offset}, #{limit}

		SELECT
		device.id as deviceId,
		device.`name` AS deviceName,
		patrolrecord.`id` as recordId,
		patrolrecord.`work_orderNo` AS workOrderNo,
		patrolperson.`name` AS patrolpersonName,
		patrolperson.`user_id` AS patrolpersonId,
		patrolrecord.`name` AS recordName,
		patrolproject.`name` as projectName,
		patrolproject.`function` as projectFunction,
		patroldetail.`result` as projectResult,
		IF(patrolrecord.end_time < NOW() and dict.`id`=56,'超期未处理',dict.`name`) AS recordStatusName,
		IF(patrolrecord.end_time < NOW() and dict.`id`=56,999,dict.`id`) AS recordStatusId,
		patrolrecord.end_time AS endTime,
		patrolrecord.`complete_time` as completeTime
		FROM cus_patrol_detail patroldetail
		LEFT JOIN  cus_patrol_project patrolproject ON patrolproject.`id`=patroldetail.`project_id`
		LEFT JOIN  cus_patrol_record patrolrecord ON patrolrecord.`id`=patroldetail.`record_id`
		LEFT JOIN  sys_user patrolperson ON patrolperson.`user_id`=patrolrecord.`engineer_id`
		LEFT JOIN  cus_dictionary dict ON dict.id=patrolrecord.`status`
		LEFT JOIN  cus_device device ON device.`id`=patroldetail.`device_id`

		  		   and patroldetail.id = #{id} 
		  		   and patroldetail.device_id = #{deviceId} 
		  		   and patroldetail.project_id = #{projectId} 
		  		   and patrolrecord.status = #{recordStatus} 
		  		   and patroldetail.result = #{result} 
		  		   AND patrolrecord.end_time < NOW() AND dict.`id` = #{statusForBoard} 
		  		   and patroldetail.status = #{status} 
		  		   and patroldetail.content = #{content} 
		  		   and patroldetail.record_id = #{recordId} 
		  		    and  DATE_FORMAT(patrolrecord.`complete_time`,'%Y-%m-%d') >=  DATE_FORMAT(#{startTime},'%Y-%m-%d')
				    and  DATE_FORMAT(patrolrecord.`complete_time`,'%Y-%m-%d') <=  DATE_FORMAT(#{endTime},'%Y-%m-%d')



                ORDER BY ${sort} ${order}


				ORDER BY patrolrecord.end_time ASC,patroldetail.id DESC


                ORDER BY patroldetail.id desc



			limit #{offset}, #{limit}

SELECT
COUNT(*)
FROM cus_patrol_detail patroldetail
LEFT JOIN  cus_patrol_project patrolproject ON patrolproject.`id`=patroldetail.`project_id`
LEFT JOIN  cus_patrol_record patrolrecord ON patrolrecord.`id`=patroldetail.`record_id`
LEFT JOIN  sys_user patrolperson ON patrolperson.`user_id`=patrolrecord.`engineer_id`
LEFT JOIN  cus_dictionary dict ON dict.id=patrolrecord.`status`
LEFT JOIN  cus_device device ON device.`id`=patroldetail.`device_id`

  		   and patroldetail.id = #{id} 
  		   and patroldetail.device_id = #{deviceId} 
  		   and patroldetail.project_id = #{projectId} 
  		   and patrolrecord.status = #{recordStatus} 
  		   and patroldetail.result = #{result} 
	 AND patrolrecord.end_time < NOW() AND dict.`id` = #{statusForBoard} 
  		   and patroldetail.status = #{status} 
  		   and patroldetail.content = #{content} 
  		   and patroldetail.record_id = #{recordId} 
  		    and  DATE_FORMAT(patrolrecord.`complete_time`,'%Y-%m-%d') >=  DATE_FORMAT(#{startTime},'%Y-%m-%d')
		    and  DATE_FORMAT(patrolrecord.`complete_time`,'%Y-%m-%d') <=  DATE_FORMAT(#{endTime},'%Y-%m-%d')

SELECT COUNT(*)
FROM(SELECT COUNT(1)
FROM cus_patrol_detail patroldetail
LEFT JOIN  cus_patrol_record patrolrecord ON patrolrecord.`id`=patroldetail.`record_id`
LEFT JOIN  cus_device device ON device.`id`=patroldetail.`device_id`
LEFT JOIN cus_dictionary dict ON dict.id=patrolrecord.`status`

  		   and patroldetail.id = #{id} 
  		   and patroldetail.`device_id` = #{deviceId} 
  		   and patroldetail.result = #{result} 
  		   and patroldetail.record_id = #{recordId} 
		   and patroldetail.create_by = #{createBy} 
		   and patroldetail.create_time = #{createTime} 
		   and patroldetail.update_by = #{updateBy} 
		   and patroldetail.update_time = #{updateTime} 
		    and  DATE_FORMAT(patrolrecord.`complete_time`,'%Y-%m-%d') >=  DATE_FORMAT(#{startTime},'%Y-%m-%d')
		    and  DATE_FORMAT(patrolrecord.`complete_time`,'%Y-%m-%d') <=  DATE_FORMAT(#{endTime},'%Y-%m-%d')
		   and patroldetail.del_flag = #{delFlag} 						  		   

GROUP BY patrolrecord.id) T

