mapper.select:
		select `id`,`link_patrol_plan_id`,`link_device_id`,`link_project_id`,`create_by`,`create_time`,`update_by`,`update_time`,`del_flag` from cus_patrol_plan_detail

		  		   and id = #{id} 
		  		   and link_patrol_plan_id = #{linkPatrolPlanId} 
		  		   and link_device_id = #{linkDeviceId} 
             and link_project_id = #{linkProjectId} 
				   and create_by = #{createBy} 
				   and create_time = #{createTime} 
				   and update_by = #{updateBy} 
				   and update_time = #{updateTime} 
				   and del_flag = #{delFlag} 		  		  



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

		SELECT cppd.`id` as id,
		cppd.`link_project_id` as linkProjectId,
		cpp.`work_orderNo` as workOrderNo,
		cde.`id` as deviceId,
		cde.`serialno` as serialno,
		cde.`name` as cdName 
		FROM cus_patrol_plan_detail cppd 
		LEFT JOIN cus_patrol_plan cpp ON cpp.id=cppd.link_patrol_plan_id 
		LEFT JOIN cus_device cde ON cde.id=cppd.link_device_id

		  		   and id = #{id} 
		  		   and cppd.link_patrol_plan_id = #{linkPatrolPlanId} 
		  		   and cppd.link_device_id = #{linkDeviceId} 
		  		   and cppd.link_project_id = #{linkProjectId} 



                order by ${sort} ${order}


                order by cppd.id desc



			limit #{offset}, #{limit}

		select count(*) from cus_patrol_plan_detail

		  		   and id = #{id} 
		  		   and link_patrol_plan_id = #{linkPatrolPlanId} 
		  		   and link_device_id = #{linkDeviceId} 
              and REPLACE(REPLACE(link_project_id,'[',','),']',',') LIKE #{linkProjectId} 
		  		   and create_by = #{createBy} 
				   and create_time = #{createTime} 
				   and update_by = #{updateBy} 
				   and update_time = #{updateTime} 
				   and del_flag = #{delFlag}
