select count(*) from cus_patrol_record

  		   and id = #{id} 
  		   and work_orderNo = #{workOrderno} 
  		   and name = #{name} 
  		   and engineer_id = #{engineerId} 
  		   and cellphone = #{cellphone} 
  		   and start_time = #{startTime} 
  		   and end_time = #{endTime} 
  		   and status = #{status} 
  		   and content = #{content} 
  		   and result = #{result} 
  		   and plan_id = #{planId} 
  		   and inform_id = #{informId} 
  		   and complete_time = #{completeTime} 
		   and create_by = #{createBy} 
		   and create_time = #{createTime} 
		   and update_by = #{updateBy} 
		   and update_time = #{updateTime} 
		   and del_flag = #{delFlag}

SELECT 
	COUNT(*) 
FROM
		cus_patrol_record patrolrecord
	LEFT JOIN cus_patrol_plan patrolplan ON patrolplan.`id` = patrolrecord.`plan_id`
	LEFT JOIN sys_user patrolperson ON patrolperson.`user_id` = patrolrecord.`engineer_id`
	LEFT JOIN sys_dept patroldept ON patroldept.dept_id = patrolperson.dept_id
	LEFT JOIN sys_user createperson ON createperson.user_id = patrolrecord.`create_by`
	LEFT JOIN sys_dept createdept ON createdept.dept_id = createperson.`dept_id`
	LEFT JOIN cus_dictionary dict ON dict.id = patrolrecord.`status`

  		 and patrolrecord.id = #{id} 
  		  and patrolperson.`user_id` = #{engineerId} 
  		   and IF(patrolrecord.end_time < NOW() and dict.`id`=56,999,dict.`id`) = #{status} 
  		   and dict.`id` = #{singleStatus} 
  		  	 and patrolperson.`user_id` =#{user} and patrolrecord.`start_time` < now()

  		   and patrolrecord.`work_orderNo` = #{workOrderno} 
  		   and patrolplan.`create_by` = #{createBy} 
  		   and (patrolrecord.`engineer_id`=#{userId} OR ( patrolplan.`create_by`=#{userId} and patrolrecord.status <> 146)) 
  		    and t.`idPath` LIKE CONCAT(#{deptId},'%')
