mapper.select:
		select `id`,`total_number`,`pay_method`,`pay_date`,`pay_user`,`opening_blank`,`blank_account`,`pay_reason`,`create_by`,`create_time`,`update_by`,`update_time`,`status`,`process_instance_id`,`del_flag` from cus_pay_apply

		  		   and id = #{id} 
		  		   and total_number = #{totalNumber} 
		  		   and pay_method = #{payMethod} 
		  		   and pay_date = #{payDate} 
		  		   and pay_user = #{payUser} 
		  		   and opening_blank = #{openingBlank} 
		  		   and blank_account = #{blankAccount} 
		  		   and pay_reason = #{payReason} 
		  		   and create_by = #{createBy} 
		  		   and create_time = #{createTime} 
		  		   and update_by = #{updateBy} 
		  		   and update_time = #{updateTime} 
		  		   and status = #{status} 
		  		   and process_instance_id = #{processInstanceId} 
		  		   and del_flag = #{delFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from cus_pay_apply

  		   and id = #{id} 
  		   and total_number = #{totalNumber} 
  		   and pay_method = #{payMethod} 
  		   and pay_date = #{payDate} 
  		   and pay_user = #{payUser} 
  		   and opening_blank = #{openingBlank} 
  		   and blank_account = #{blankAccount} 
  		   and pay_reason = #{payReason} 
  		   and create_by = #{createBy} 
  		   and create_time = #{createTime} 
  		   and update_by = #{updateBy} 
  		   and update_time = #{updateTime} 
  		   and status = #{status} 
  		   and process_instance_id = #{processInstanceId} 
  		   and del_flag = #{delFlag}

select * from
(SELECT
apply.id as id,
apply.total_number as totalnumber,
apply.pay_method as paymethod,
apply.pay_date as paydate,
apply.pay_user as payuser,
apply.opening_blank as openingblank,
apply.blank_account as blankaccount,
apply.pay_reason as payreason,
apply.create_time as createtime,
apply.create_by as createby,
createuser.user_id as createuserid,
createuser.name as createusername,
apply.update_by as updateby,
apply.update_time as updatetime,
apply.del_flag as delflag,
apply.status as statusid,
statusdic.value as statusvalue,
statusdic.name as statusname,
assignuser.user_id as assignid,
createdept.dept_id as createdeptid,
createdept.id_path as createIdPath,
createdept.name as createdeptname,
payuser.user_id as payuserid,
payuser.name as payusername,
paydept.dept_id as paydeptid,
paydept.name as paydeptname,
apply.process_instance_id as processinstanceid
FROM
cus_pay_apply apply
LEFT JOIN act_ru_task task on task.PROC_INST_ID_ = apply.process_instance_id
LEFT JOIN sys_user createuser on createuser.user_id = apply.create_by
LEFT JOIN sys_dept createdept on createdept.dept_id = createuser.dept_id
LEFT JOIN sys_user payuser on payuser.user_id = apply.pay_user
LEFT JOIN sys_dept paydept on paydept.dept_id = payuser.dept_id
LEFT JOIN sys_user assignuser on assignuser.user_id = task.ASSIGNEE_
LEFT JOIN cus_dictionary statusdic on statusdic.id = apply.status) view_pay_apply

	 and id = #{id} 
	 and totalnumber = #{totalNumber} 
	 and paymethod = #{payMethod} 
	 and paydate = #{payDate} 
	 and payuser = #{payUser} 
	 and openingblank = #{openingBlank} 
	 and blankaccount = #{blankAccount} 
	 and payreason = #{payReason} 
	 and createtime = #{createTime} 
	 and updateby = #{updateBy} 
	 and updatetime = #{updateTime} 
	 and processinstanceid = #{processInstanceId} 
	 and delflag = #{delFlag} 
	 and createdeptid = #{deptId} 
	 and date_format(createtime,'%Y-%m-%d') >= #{beginTime} 
	 and date_format(createtime,'%Y-%m-%d') <= #{endTime} 

	 and  DATE_FORMAT(createtime,'%Y-%m-%d') >=  DATE_FORMAT(#{beginTimes},'%Y-%m-%d')
	  and  DATE_FORMAT(createtime,'%Y-%m-%d') <=  DATE_FORMAT(#{endTimes},'%Y-%m-%d')

	 and statusid != #{status} 
	 and statusid != #{statusOther} 


	 and createIdPath like CONCAT (#{idPath},'%') 
	 and assignid = #{assignId} 
	 and createby = #{createBy} 
	 and createusername like CONCAT (#{createByName},'%') 




		order by ${sort} ${order}


		order by  id desc , createtime DESC ,statusid desc



	limit #{offset}, #{limit}

select
count(1) as counts ,
sum(totalnumber) as allTotalNumber
from
(SELECT
apply.id as id,
apply.total_number as totalnumber,
apply.pay_method as paymethod,
apply.pay_date as paydate,
apply.pay_user as payuser,
apply.opening_blank as openingblank,
apply.blank_account as blankaccount,
apply.pay_reason as payreason,
apply.create_time as createtime,
apply.create_by as createby,
createuser.user_id as createuserid,
createuser.name as createusername,
apply.update_by as updateby,
apply.update_time as updatetime,
apply.del_flag as delflag,
apply.status as statusid,
statusdic.value as statusvalue,
statusdic.name as statusname,
assignuser.user_id as assignid,
createdept.dept_id as createdeptid,
createdept.id_path as createIdPath,
createdept.name as createdeptname,
payuser.user_id as payuserid,
payuser.name as payusername,
paydept.dept_id as paydeptid,
paydept.name as paydeptname,
apply.process_instance_id as processinstanceid
FROM
cus_pay_apply apply
LEFT JOIN act_ru_task task on task.PROC_INST_ID_ = apply.process_instance_id
LEFT JOIN sys_user createuser on createuser.user_id = apply.create_by
LEFT JOIN sys_dept createdept on createdept.dept_id = createuser.dept_id
LEFT JOIN sys_user payuser on payuser.user_id = apply.pay_user
LEFT JOIN sys_dept paydept on paydept.dept_id = payuser.dept_id
LEFT JOIN sys_user assignuser on assignuser.user_id = task.ASSIGNEE_
LEFT JOIN cus_dictionary statusdic on statusdic.id = apply.status) view_pay_apply


	 and id = #{id} 
	 and totalnumber = #{totalNumber} 
	 and paymethod = #{payMethod} 
	 and paydate = #{payDate} 
	 and payuser = #{payUser} 
	 and openingblank = #{openingBlank} 
	 and blankaccount = #{blankAccount} 
	 and payreason = #{payReason} 
	 and createtime = #{createTime} 
	 and updateby = #{updateBy} 
	 and updatetime = #{updateTime} 
	 and processinstanceid = #{processInstanceId} 
	 and delflag = #{delFlag} 
	 and createdeptid = #{deptId} 
	 and date_format(createtime,'%Y-%m-%d') >= #{beginTime} 
	 and date_format(createtime,'%Y-%m-%d') <= #{endTime} 

	 and  DATE_FORMAT(createtime,'%Y-%m-%d') >=  DATE_FORMAT(#{beginTimes},'%Y-%m-%d')
	  and  DATE_FORMAT(createtime,'%Y-%m-%d') <=  DATE_FORMAT(#{endTimes},'%Y-%m-%d')

	 and statusid != #{status} 
	 and statusid != #{statusOther} 
	 and statusid = #{statusOfCount} 

	 and createIdPath like CONCAT (#{idPath},'%') 
	 and assignid = #{assignId} 
	 and createby = #{createBy} 
	 and createusername like CONCAT (#{createByName},'%')
