mapper.select:
		select `id`,`username`,`pay_number`,`pay_date`,`payee`,`refund_amount`,`drawee`,`pay_account`,`settlement_method`,`create_by`,`create_time`,`update_by`,`update_time`,`del_flag` from cus_paydetails

		  		   and id = #{id} 
		  		   and username = #{username} 
		  		   and pay_number = #{payNumber} 
		  		   and pay_date = #{payDate} 
		  		   and payee = #{payee} 
		  		   and refund_amount = #{refundAmount} 
		  		   and drawee = #{drawee} 
		  		   and pay_account = #{payAccount} 
		  		   and settlement_method = #{settlementMethod} 
		  		   and create_by = #{createBy} 
		  		   and create_time = #{createTime} 
		  		   and update_by = #{updateBy} 
		  		   and update_time = #{updateTime} 
		  		   and del_flag = #{delFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from cus_paydetails

