mapper.select:
		select `pid`,`document_date`,`due_date`,`pay_money`,`amount_paid`,`this_pay`,`unpay_money`,`pay_account`,`settlement_method`,`settlement_num`,`remarks`,`source_num`,`source_type`,`paymentform_id`,`del_flag` from cus_paymentform_item

		  		   and pid = #{pid} 
		  		   and document_date = #{documentDate} 
		  		   and due_date = #{dueDate} 
		  		   and pay_money = #{payMoney} 
		  		   and amount_paid = #{amountPaid} 
		  		   and this_pay = #{thisPay} 
		  		   and unpay_money = #{unpayMoney} 
		  		   and pay_account = #{payAccount} 
		  		   and settlement_method = #{settlementMethod} 
		  		   and settlement_num = #{settlementNum} 
		  		   and remarks = #{remarks} 
		  		   and source_num = #{sourceNum} 
		  		   and source_type = #{sourceType} 
		  		   and paymentform_id = #{paymentformId} 
		  		   and del_flag = #{delFlag} 



                order by ${sort} ${order}


                order by pid desc



			limit #{offset}, #{limit}

select count(*) from cus_paymentform_item

  		   and pid = #{pid} 
