mapper.select:
		select `id`,`company`,`total_receivables`,`pay_date`,`drawee`,`pay_num`,`pay_type`,`audit_status`,`auditor`,`auditor_time`,`create_by`,`create_time`,`update_by`,`update_time`,`del_flag` from cus_paymentform

		  		   and id = #{id} 
		  		   and company = #{company} 
		  		   and total_receivables = #{totalReceivables} 
		  		   and pay_date = #{payDate} 
		  		   and drawee = #{drawee} 
		  		   and pay_num = #{payNum} 
		  		   and pay_type = #{payType} 
		  		   and LEFT(inhead_code,12) = #{maxNo} 
		  		   and audit_status = #{auditStatus} 
		  		   and auditor = #{auditor} 
		  		   and auditor_time = #{auditorTime} 
		  		   and create_by = #{createBy} 
		  		   and create_time = #{createTime} 
		  		   and update_by = #{updateBy} 
		  		   and update_time = #{updateTime} 
		  		   and del_flag = #{delFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from cus_paymentform

  		   and id = #{id} 
  		   and company = #{company} 
  		   and total_receivables = #{totalReceivables} 
  		   and pay_date = #{payDate} 
  		   and drawee = #{drawee} 
  		   and pay_num = #{payNum} 
  		   and pay_type = #{payType} 
  		   and audit_status = #{auditStatus} 
  		   and auditor = #{auditor} 
  		   and auditor_time = #{auditorTime} 
  		   and create_by = #{createBy} 
  		   and create_time = #{createTime} 
  		   and update_by = #{updateBy} 
  		   and update_time = #{updateTime} 
  		   and del_flag = #{delFlag}

		select payf.`id`,company.name as `company`,`total_receivables` as `totalReceivables`,`pay_date` as `payDate`,`drawee`,`pay_num` as `payNum`,dictc.name as `payType`,
			   dict.name as `auditStatus`,usera.username as `auditor`,`auditor_time` as `auditorTime`,userc.username as `createBy`,payf.`create_time` as `createTime`,
			   useru.username as `updateBy`,payf.`update_time` as `updateTime`,payf.`del_flag` as `delFlag`,`pid`,`document_date` as `documentDate`,`due_date` as `dueDate`,
			   `pay_money` as `payMoney`,`amount_paid` as `amountPaid`,`this_pay` as `thisPay`,`unpay_money` as `unpayMoney`,`pay_account` as `payAccount`,
			   dictm.name as `settlementMethod`,`settlement_num` as `settlementNum`,`remarks`,`source_num` as `sourceNum`,dicts.name as `sourceType`,
			   `paymentform_id` as `paymentformId`,payfitem.`del_flag` as `delFlag`
			from cus_paymentform payf left join cus_paymentform_item payfitem on payf.id = payfitem.`paymentform_id` 
				left join cus_dictionary dict on payf.audit_status = dict.id left join sys_company company on payf.company = company.id
				left join sys_user usera on payf.auditor = usera.user_id left join sys_user userc on payf.create_by = userc.user_id 
				left join sys_user useru on payf.update_by = useru.user_id left join cus_dictionary dicts on payfitem.source_type = dicts.id
				left join cus_dictionary dictc on payf.pay_type = dictc.id left join cus_dictionary dictm on payfitem.settlement_method = dictm.id

		  		    and  DATE_FORMAT(pay_date,'%Y-%m-%d') >=  DATE_FORMAT(#{startTime},'%Y-%m-%d')
				    and  DATE_FORMAT(pay_date,'%Y-%m-%d') <=  DATE_FORMAT(#{endTime},'%Y-%m-%d')
				   and pay_num = #{payNum} 
				   and company = #{company} 
				   and source_num = #{sourceNum} 
				   and drawee = #{drawee} 
				   and pay_account = #{payAccount} 
				   and settlement_method = #{settlementMethod} 
		  		   and del_flag = #{delFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from cus_paymentform payf left join cus_paymentform_item payfitem on payf.id = payfitem.`paymentform_id` 
		left join cus_dictionary dict on payf.audit_status = dict.id left join sys_company company on payf.company = company.id
		left join sys_user usera on payf.auditor = usera.user_id left join sys_user userc on payf.create_by = userc.user_id 
		left join sys_user useru on payf.update_by = useru.user_id left join cus_dictionary dicts on payfitem.source_type = dicts.id
		left join cus_dictionary dictc on payf.pay_type = dictc.id left join cus_dictionary dictm on payfitem.settlement_method = dictm.id

  		    and  DATE_FORMAT(pay_date,'%Y-%m-%d') >=  DATE_FORMAT(#{startTime},'%Y-%m-%d')
		    and  DATE_FORMAT(pay_date,'%Y-%m-%d') <=  DATE_FORMAT(#{endTime},'%Y-%m-%d')
		   and pay_num = #{payNum} 
		   and company = #{company} 
		   and source_num = #{sourceNum} 
		   and drawee = #{drawee} 
		   and pay_account = #{payAccount} 
		   and settlement_method = #{settlementMethod} 
  		   and del_flag = #{delFlag}
