mapper.select:
		SELECT 
		qualityreport.id as id,
		qualitygroup.id as gid,
		qualitygroup.responsible_content as responsibleContent,
		person.`name` as personName,
		person.`user_id` as personId,
		dept.`name` as deptName,
		dept.`dept_id` as deptId 
		FROM
		cus_quality_report qualityreport
		LEFT JOIN cus_quality_group qualitygroup ON qualitygroup.`report_id`=qualityreport.id
		LEFT JOIN sys_user person ON person.user_id=qualitygroup.`user_id`
		LEFT JOIN sys_dept dept on dept.dept_id=person.dept_id

		  		   and qualitygroup.id = #{id} 
		  		   and qualitygroup.report_id = #{reportId} 
		  		   and qualitygroup.dept_id = #{deptId} 
		  		   and qualitygroup.user_id = #{userId} 
		  		   and qualitygroup.responsible_content = #{responsibleContent} 
		  		   and qualitygroup.create_time = #{createTime} 
		  		   and qualitygroup.create_by = #{createBy} 
		  		   and qualitygroup.update_by = #{updateBy} 
		  		   and qualitygroup.update_time = #{updateTime} 
		  		   and qualitygroup.del_flag = #{delFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

		select `id`,`report_id`,`dept_id`,`user_id`,`responsible_content`,`create_time`,`create_by`,`update_by`,`update_time`,`del_flag` from cus_quality_group

		  		   and id = #{id} 
		  		   and report_id = #{reportId} 
		  		   and dept_id = #{deptId} 
		  		   and user_id = #{userId} 
		  		   and responsible_content = #{responsibleContent} 
		  		   and create_time = #{createTime} 
		  		   and create_by = #{createBy} 
		  		   and update_by = #{updateBy} 
		  		   and update_time = #{updateTime} 
		  		   and del_flag = #{delFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from cus_quality_group

  		   and id = #{id} 
  		   and report_id = #{reportId} 
  		   and dept_id = #{deptId} 
