mapper.select:
		select `rid`,`document_date`,`receivable_date`,`receivable_money`,`received_money`,`this_receivable`,`unpay_money`,`pay_account`,`settlement_method`,`settlement_num`,`remarks`,`source_type`,`source_num`,`receipt_id`,`del_flag` from cus_receipt_item

		  		   and rid = #{rid} 
		  		   and document_date = #{documentDate} 
		  		   and receivable_date = #{receivableDate} 
		  		   and receivable_money = #{receivableMoney} 
		  		   and received_money = #{receivedMoney} 
		  		   and this_receivable = #{thisReceivable} 
		  		   and unpay_money = #{unpayMoney} 
		  		   and pay_account = #{payAccount} 
		  		   and settlement_method = #{settlementMethod} 
		  		   and settlement_num = #{settlementNum} 
		  		   and remarks = #{remarks} 
		  		   and source_type = #{sourceType} 
		  		   and source_num = #{sourceNum} 
		  		   and receipt_id = #{receiptId} 
		  		   and del_flag = #{delFlag} 



                order by ${sort} ${order}


                order by rid desc



			limit #{offset}, #{limit}

select count(*) from cus_receipt_item

