mapper.select:
		select `id`,`username`,`total_receivables`,`receivables_date`,`payee`,`collection_type`,`receipt_num`,`audit_status`,`auditor`,`auditor_time`,`create_by`,`create_time`,`update_by`,`update_time`,`del_flag` from cus_receipt

		  		   and id = #{id} 
		  		   and username = #{username} 
		  		   and total_receivables = #{totalReceivables} 
		  		   and receivables_date = #{receivablesDate} 
		  		   and payee = #{payee} 
		  		   and collection_type = #{collectionType} 
		  		   and receipt_num = #{receiptNum} 
		  		   and LEFT(inhead_code,12) = #{maxNo} 
		  		   and audit_status = #{auditStatus} 
		  		   and auditor = #{auditor} 
		  		   and auditor_time = #{auditorTime} 
		  		   and create_by = #{createBy} 
		  		   and create_time = #{createTime} 
		  		   and update_by = #{updateBy} 
		  		   and update_time = #{updateTime} 
		  		   and del_flag = #{delFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from cus_receipt

  		   and id = #{id} 
  		   and username = #{username} 
  		   and total_receivables = #{totalReceivables} 
  		   and receivables_date = #{receivablesDate} 
  		   and payee = #{payee} 
  		   and collection_type = #{collectionType} 
  		   and receipt_num = #{receiptNum} 
  		   and audit_status = #{auditStatus} 
  		   and auditor = #{auditor} 
  		   and auditor_time = #{auditorTime} 
  		   and create_by = #{createBy} 
  		   and create_time = #{createTime} 
  		   and update_by = #{updateBy} 
  		   and update_time = #{updateTime} 
  		   and del_flag = #{delFlag}

		select recp.`id`,recp.`username`,`total_receivables` as `totalReceivables`,`receivables_date` as `receivablesDate`,`payee`,dictc.name as `collectionType`,
			   `receipt_num` as `receiptNum`,dict.name as `auditStatus`,usera.username as `auditor`,recp.`auditor_time` as `auditorTime`,userc.username as `createBy`,
			   recp.`create_time` as `createTime`,useru.username as `updateBy`,recp.`update_time` as `updateTime`,recp.`del_flag` as `delFlag`,`rid`,`document_date` as 
			   `documentDate`,`receivable_date` as `receivableDate`,`receivable_money` as `receivableMoney`,`received_money` as `receivedMoney`,`this_receivable` as 
			   `thisReceivable`,`unpay_money` as `unpayMoney`,`pay_account` as `payAccount`,dictm.name as `settlementMethod`,`settlement_num` as `settlementNum`,
				`remarks`,dicts.name as `sourceType`,`source_num` as `sourceNum`,`receipt_id` as `receiptId`,`rdel_flag` as `rdelFlag`
				from cus_receipt recp left join cus_receipt_item recpitem on recp.id = recpitem.`receipt_id` 
				left join cus_dictionary dict on recp.audit_status = dict.id
				left join sys_user usera on recp.auditor = usera.user_id left join sys_user userc on recp.create_by = userc.user_id 
				left join sys_user useru on recp.update_by = useru.user_id left join cus_dictionary dicts on recpitem.source_type = dicts.id
				left join cus_dictionary dictc on recp.collection_type = dictc.id left join cus_dictionary dictm on recpitem.settlement_method = dictm.id

		  		    and  DATE_FORMAT(invoice_date,'%Y-%m-%d') >=  DATE_FORMAT(#{startTime},'%Y-%m-%d')
				    and  DATE_FORMAT(invoice_date,'%Y-%m-%d') <=  DATE_FORMAT(#{endTime},'%Y-%m-%d')
				   and receipt_num = #{receiptNum} 
				   and recp.username = #{username} 
				   and source_num = #{sourceNum} 
				   and payee = #{payee} 
				   and pay_account = #{payAccount} 
				   and settlement_method = #{settlementMethod} 
		  		   and del_flag = #{delFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from cus_receipt recp left join cus_receipt_item recpitem on recp.id = recpitem.`receipt_id` 
		left join cus_dictionary dict on recp.audit_status = dict.id
		left join sys_user usera on recp.auditor = usera.user_id left join sys_user userc on recp.create_by = userc.user_id 
		left join sys_user useru on recp.update_by = useru.user_id left join cus_dictionary dicts on recpitem.source_type = dicts.id
		left join cus_dictionary dictc on recp.collection_type = dictc.id left join cus_dictionary dictm on recpitem.settlement_method = dictm.id

  		    and  DATE_FORMAT(invoice_date,'%Y-%m-%d') >=  DATE_FORMAT(#{startTime},'%Y-%m-%d')
		    and  DATE_FORMAT(invoice_date,'%Y-%m-%d') <=  DATE_FORMAT(#{endTime},'%Y-%m-%d')
		   and receipt_num = #{receiptNum} 
		   and recp.username = #{username} 
		   and source_num = #{sourceNum} 
		   and payee = #{payee} 
		   and pay_account = #{payAccount} 
		   and settlement_method = #{settlementMethod} 
  		   and del_flag = #{delFlag}
