mapper.select:
		select `id`,`account_receivable`,`description`,`create_by`,`create_time`,`update_by`,`update_time`,`status`,`process_instance_id`,`del_flag` from cus_reim_apply

		  		   and id = #{id} 
		  		   and account_receivable = #{accountReceivable} 
		  		   and description = #{description} 
		  		   and create_by = #{createBy} 
		  		   and create_time = #{createTime} 
		  		   and update_by = #{updateBy} 
		  		   and update_time = #{updateTime} 
		  		   and status = #{status} 
		  		   and process_instance_id = #{processInstanceId} 
		  		   and del_flag = #{delFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from cus_reim_apply

  		   and id = #{id} 
  		   and account_receivable = #{accountReceivable} 
  		   and description = #{description} 
  		   and create_by = #{createBy} 
  		   and create_time = #{createTime} 
  		   and update_by = #{updateBy} 
  		   and update_time = #{updateTime} 
  		   and status = #{status} 
  		   and process_instance_id = #{processInstanceId} 
  		   and del_flag = #{delFlag}

select * from ( SELECT b.oneReiCount, views.reimType as reimType, views.reimTypeName as reimTypeName, apply.id as id, apply.account_receivable as accountreceivable, apply.description as description, apply.create_by as createby, createuser.name as createuser, apply.create_time as createtime, apply.update_by as updateby, apply.update_time as updatetime, apply.del_flag as delflag, apply.status as statusId, statusdic.value as statusvalue, statusdic.name as statusname, assignuser.user_id as assignid, dept.dept_id as deptid, dept.id_path as idPath, dept.name as deptname, apply.process_instance_id as processinstanceid FROM cus_reim_apply apply
LEFT JOIN view_reim_apply_item_type views on apply.id = views.reim_apply_id LEFT JOIN act_ru_task task on task.PROC_INST_ID_ = apply.process_instance_id LEFT JOIN sys_user createuser on createuser.user_id = apply.create_by LEFT JOIN sys_dept dept on dept.dept_id = createuser.dept_id LEFT JOIN sys_user assignuser on assignuser.user_id = task.ASSIGNEE_ LEFT JOIN cus_dictionary statusdic on statusdic.id = apply.status LEFT JOIN view_reim_apply_onereimcount b on apply.id = b.reim_apply_id) view_reim_apply
and id = #{id} and accountreceivable = #{accountReceivable} and description = #{description}
and createby = #{createBy} and createuser like CONCAT (#{createByName},'%')
and statusId != #{status} and statusId != #{statusOther}
and idPath like CONCAT (#{idPath},'%') and assignid = #{assignId}
and createtime = #{createTime} and updateby = #{updateBy} and updatetime = #{updateTime}
and processinstanceid = #{processInstanceId} and delflag = #{delFlag}
and deptid = #{deptId}
and DATE_FORMAT(createtime,'%Y-%m-%d') >= DATE_FORMAT(#{beginTime},'%Y-%m-%d') and DATE_FORMAT(createtime,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d')
order by ${sort} ${order}
order by id desc , createtime DESC ,statusId desc
limit #{offset}, #{limit}

select
count(1) as counts ,
sum(reiCount) as allReiCount
from (
SELECT
a.oneReiCount as reiCount,
apply.id as id,
apply.account_receivable as accountreceivable,
apply.description as description,
apply.create_by as createby,
createuser.name as createuser,
apply.create_time as createtime,
apply.update_by as updateby,
apply.update_time as updatetime,
apply.del_flag as delflag,
apply.status as statusId,
statusdic.value as statusvalue,
statusdic.name as statusname,
assignuser.user_id as assignid,
dept.dept_id as deptid,
dept.id_path as idPath,
dept.name as deptname,
apply.process_instance_id as processinstanceid
FROM
cus_reim_apply apply
LEFT JOIN view_reim_apply_onereimcount a on apply.id = a.reim_apply_id
LEFT JOIN act_ru_task task on task.PROC_INST_ID_ = apply.process_instance_id
LEFT JOIN sys_user createuser on createuser.user_id = apply.create_by
LEFT JOIN sys_dept dept on dept.dept_id = createuser.dept_id
LEFT JOIN sys_user assignuser on assignuser.user_id = task.ASSIGNEE_
LEFT JOIN cus_dictionary statusdic on statusdic.id = apply.status
)
view_reim_apply

	 and id = #{id} 
	 and accountreceivable = #{accountReceivable} 
	 and description = #{description} 


	 and createby = #{createBy} 
	 and createuser like CONCAT (#{createByName},'%')

	 and statusId != #{status} 
	 and statusId != #{statusOther} 
	 and statusId = #{statusOfCount} 

	 and idPath like CONCAT (#{idPath},'%') 
	 and assignid = #{assignId} 


	 and createtime = #{createTime} 
	 and updateby = #{updateBy} 
	 and updatetime = #{updateTime} 

	 and processinstanceid = #{processInstanceId} 
	 and delflag = #{delFlag} 

	 and deptid = #{deptId} 

	 and  DATE_FORMAT(createtime,'%Y-%m-%d') >=  DATE_FORMAT(#{beginTime},'%Y-%m-%d')
	  and  DATE_FORMAT(createtime,'%Y-%m-%d') <=  DATE_FORMAT(#{endTime},'%Y-%m-%d')
