mapper.select:
		select `id`,`result`,`content`,`create_by`,`create_time`,`event_id`,`record_id`,`xl_grade`,`qa_grade`,`td_grade`,`update_by`,`update_time`,`del_flag` from cus_repair_check

		  		   and id = #{id} 
		  		   and result = #{result} 
		  		   and content = #{content} 
		  		   and create_by = #{createBy} 
		  		   and create_time = #{createTime} 
		  		   and event_id = #{eventId} 
		  		   and record_id = #{recordId} 
		  		   and xl_grade = #{xlGrade} 
		  		   and qa_grade = #{qaGrade} 
		  		   and td_grade = #{tdGrade} 
				   and update_by = #{updateBy} 
				   and update_time = #{updateTime} 
				   and del_flag = #{delFlag} 		  		  



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

		select 
		repaircheck.`id`,
		repaircheck.`result`,
		dics.`name` AS resultName,
		createuser.`name` AS createName,
		repaircheck.`content`,
		repaircheck.`create_by`,
		repaircheck.`create_time`,
		repaircheck.`event_id`,
		repaircheck.`record_id`,
		repaircheck.`xl_grade`,
		repaircheck.`qa_grade`,
		repaircheck.`td_grade`,
		repaircheck.`update_by`,
		repaircheck.`update_time`,
		repaircheck.`del_flag` 
		from cus_repair_check repaircheck
		LEFT JOIN sys_user createuser ON createuser.`user_id`=repaircheck.`create_by`
		LEFT JOIN cus_dictionary dics ON dics.`id`=repaircheck.`result`

		  		   and id = #{id} 
		  		   and result = #{result} 
		  		   and content = #{content} 
		  		   and create_by = #{createBy} 
		  		   and create_time = #{createTime} 
		  		   and event_id = #{eventId} 
		  		   and record_id = #{recordId} 
		  		   and xl_grade = #{xlGrade} 
		  		   and qa_grade = #{qaGrade} 
		  		   and td_grade = #{tdGrade} 
				   and update_by = #{updateBy} 
				   and update_time = #{updateTime} 
				   and del_flag = #{delFlag} 		  		  



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from cus_repair_check

  		   and id = #{id} 
  		   and result = #{result} 
  		   and content = #{content} 
  		   and create_by = #{createBy} 
  		   and create_time = #{createTime} 
  		   and event_id = #{eventId} 
  		   and record_id = #{recordId} 
  		   and xl_grade = #{xlGrade} 
  		   and qa_grade = #{qaGrade} 
  		   and td_grade = #{tdGrade} 
		   and update_by = #{updateBy} 
		   and update_time = #{updateTime} 
		   and del_flag = #{delFlag}
