select count(*) from cus_repair_event

  		   and id = #{id} 
  		   and work_orderNo = #{workOrderno} 
  		   and type = #{type} 
  		   and level = #{level} 
  		   and usage = #{usage} 
  		   and engineer_id = #{engineerId} 
  		   and plan_time = #{planTime} 
  		   and man_hour = #{manHour} 
  		   and cost = #{cost} 
  		   and status = #{status} 
  		   and content = #{content} 
  		   and device_id = #{deviceId} 
  		   and create_by = #{createBy} 
  		   and create_time = #{createTime} 
  		   and parent_id = #{parentId} 
  		   and parent_type = #{parentType} 
		   and update_by = #{updateBy} 
		   and update_time = #{updateTime} 
		   and del_flag = #{delFlag}

SELECT 
COUNT(*)
FROM  cus_repair_event repairevent
LEFT JOIN view_repair_held_person heldemployee ON heldemployee.task_id=repairevent.id
LEFT JOIN sys_user helduser ON helduser.`user_id`=heldemployee.`held_person`
LEFT JOIN sys_dept helddept ON helddept.dept_id=helduser.dept_id
LEFT JOIN cus_dictionary dics ON dics.`id`=repairevent.`status` 
LEFT JOIN cus_dictionary dict ON dict.`id`=repairevent.`type`
LEFT JOIN cus_dictionary dicl ON dicl.`id`=repairevent.`level`
LEFT JOIN cus_device device ON device.`id`=repairevent.`device_id`
LEFT JOIN sys_user engineeruser ON engineeruser.`user_id`=repairevent.`engineer_id`
LEFT JOIN sys_dept dept ON dept.dept_id=engineeruser.dept_id
LEFT JOIN sys_user createuser ON createuser.`user_id`=repairevent.`create_by`
LEFT JOIN sys_dept createdept ON createdept.`dept_id`=createuser.`dept_id`
LEFT JOIN view_repair_cc_person ccperson ON ccperson.task_id = repairevent.id
LEFT JOIN view_repair_held_persons heldemployees ON heldemployees.task_id=repairevent.id

  		   and repairevent.id = #{id} 
  		   and repairevent.work_orderNo = #{workOrderno} 
  		   and repairevent.type = #{type} 
  		   and repairevent.level = #{level} 
  		   and repairevent.usage = #{usage} 
  		   and IFNULL(helduser.`user_id`,engineeruser.`user_id`) = #{engineerId} 
  		   and repairevent.status = #{status} 
  		   and repairevent.status = #{singleStatus} 
  		   and repairevent.device_id = #{deviceId} 
  		   and repairevent.create_by = #{createBy} 
  		   and repairevent.create_time = #{createTime} 
  		   and repairevent.parent_id = #{parentId} 
  		   and repairevent.parent_type = #{parentType} 
		   and repairevent.del_flag = #{delFlag} 
		    and  DATE_FORMAT(repairevent.create_time,'%Y-%m-%d') >=  DATE_FORMAT(#{startTime},'%Y-%m-%d')
		    and  DATE_FORMAT(repairevent.create_time,'%Y-%m-%d') <=  DATE_FORMAT(#{endTime},'%Y-%m-%d')
		   and  createdept.`id_path` LIKE CONCAT(#{deptId},'%')		
		   and IFNULL(helddept.`dept_id`,dept.`dept_id`) = #{deptIdQuery}	  
  		   and (repairevent.`create_by`=#{userId} OR ((repairevent.`engineer_id`=#{userId} OR ccperson.`cc_person` LIKE CONCAT(CONCAT(',',#{userId}),',%') OR heldemployees.`held_persons` LIKE CONCAT(CONCAT(',',#{userId}),',%') ) AND repairevent.status <> 146))
  		   and CONCAT(device.name,device.serialno) LIKE CONCAT('%',CONCAT(#{divceName},'%'))

  		   and (repairevent.status=56 OR repairevent.status=57)
