mapper.select:
select `id`,`plan_id`,`record_id`,`part_id`,`spart_amount`,`spart_price`,`spart_unit`,`spart_sum`,`remark`,`create_by`,`create_time`,`update_by`,`update_time`,`del_flag` from cus_upkeep_record_part

	 and id = #{id} 
	 and plan_id = #{planId} 
	 and record_id = #{recordId} 
	 and part_id = #{partId} 
	 and spart_amount = #{spartAmount} 
	 and spart_price = #{spartPrice} 
	 and spart_unit = #{spartUnit} 
	 and spart_sum = #{spartSum} 
	 and remark = #{remark} 
	 and create_by = #{createBy} 
	 and create_time = #{createTime} 
	 and update_by = #{updateBy} 
	 and update_time = #{updateTime} 
	 and del_flag = #{delFlag} 



		order by ${sort} ${order}


		order by id desc



	limit #{offset}, #{limit}

select count(*) from cus_upkeep_record_part

	 and id = #{id} 
	 and plan_id = #{planId} 
	 and record_id = #{recordId} 
	 and part_id = #{partId} 
	 and spart_amount = #{spartAmount} 
	 and spart_price = #{spartPrice} 
	 and spart_unit = #{spartUnit} 
	 and spart_sum = #{spartSum} 
	 and remark = #{remark} 
