mapper.select:
select `id`,`man_hour_cost`,`plan_id`,`record_id`,`project_id`,`manhour`,`result`,`remark`,`create_by`,`create_time`,`update_by`,`update_time`,`del_flag` from cus_upkeep_record_project

	 and id = #{id} 
	 and plan_id = #{planId} 
	 and record_id = #{recordId} 
	 and project_id = #{projectId} 
	 and manhour = #{manhour} 
	 and result = #{result} 
	 and remark = #{remark} 
	 and create_by = #{createBy} 
	 and create_time = #{createTime} 
	 and man_hour_cost = #{manHourCost} 
	 and update_by = #{updateBy} 
	 and update_time = #{updateTime} 
	 and del_flag = #{delFlag} 



		order by ${sort} ${order}


		order by id desc



	limit #{offset}, #{limit}

