mapper.select:
SELECT

FROM cus_week_report



		order by ${sort} ${order}


		order by id desc



	limit #{offset}, #{limit}

SELECT

	case when uassoc.sign=1 then 3
	when uassoc.sign is null then 1
	end sign,
	case when uassoc.sign=1 then '已读'
	when uassoc.sign is null then '未读'
	end readStatus,
	uassoc.user_id as userId,

week.id as id,
week.com_content as comContent,
week.content_sum as contentSum,
week.content_plan as contentPlan,
week.description as description,
week.create_by as createBy,
week.create_time as createTime,
week.update_by as updateBy,
week.update_time as updateTime,
week.del_flag as delFlag,
week.status as status,
week.start_time as startTime,
week.end_time as endTime,
b.name as `statusName`,
dept.dept_id as deptId,
dept.id_path as idPath,
dept.name as deptName,
employee.name as userName
FROM
cus_week_report week
LEFT JOIN sys_user employee ON employee.user_id = week.create_by
LEFT JOIN cus_dictionary b ON b.id = week.status
LEFT JOIN sys_dept dept ON dept.dept_id = employee.dept_id
LEFT JOIN cus_user_assoc uassoc on uassoc.assoc_id = week.id and uassoc.assoc_type = 'WEEK_REPORT_TARGET'
LEFT JOIN view_weekReport_Sender a ON week.id = a.assoc_id

	 and a.sign is null 
	 and uassoc.user_id  =#{beSentPeopleId} 

	 and id = #{id} 
	 and week.com_content = #{comContent} 
	 and week.content_sum = #{contentSum} 
	 and week.content_plan = #{contentPlan} 
	 and week.description = #{description} 
	 and employee.name like CONCAT ('%',#{userName},'%') 
	 and week.create_time = #{createTime} 
	 and  DATE_FORMAT(week.create_time,'%Y-%m-%d') >=  DATE_FORMAT(#{startTime},'%Y-%m-%d')
	  and  DATE_FORMAT(week.create_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d')
	 and week.update_by = #{updateBy} 
	 and week.update_time = #{updateTime} 
	 and week.del_flag = #{delFlag} 
	 and dept.name = #{deptName} 
	 and dept.dept_id = #{deptId} 
	 and dept.id_path like CONCAT (#{idPath},'%') 
	 and week.status = #{status} 
	 and week.create_by = #{createBy} 



		order by ${sort} ${order}


		order by week.id desc , week.create_time DESC ,week.status desc



	limit #{offset}, #{limit}

SELECT
count(1)
FROM
cus_week_report week
LEFT JOIN sys_user employee ON employee.user_id = week.create_by
LEFT JOIN cus_dictionary b ON b.id = week.status
LEFT JOIN sys_dept dept ON dept.dept_id = employee.dept_id
LEFT JOIN cus_user_assoc uassoc on uassoc.assoc_id = week.id and uassoc.assoc_type = 'WEEK_REPORT_TARGET'
LEFT JOIN view_weekReport_Sender a ON week.id = a.assoc_id

	 and a.sign is null 
	 and uassoc.user_id  =#{beSentPeopleId} 
	 and id = #{id} 
	 and week.com_content = #{comContent} 
	 and week.content_sum = #{contentSum} 
	 and week.content_plan = #{contentPlan} 
	 and week.description = #{description} 
	 and employee.name like CONCAT ('%',#{userName},'%') 
	 and week.create_time = #{createTime} 
	 and  DATE_FORMAT(week.create_time,'%Y-%m-%d') >=  DATE_FORMAT(#{startTime},'%Y-%m-%d')
	  and  DATE_FORMAT(week.create_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d')
	 and week.update_by = #{updateBy} 
	 and week.update_time = #{updateTime} 
	 and week.del_flag = #{delFlag} 
	 and dept.name = #{deptName} 
	 and dept.dept_id = #{deptId} 
	 and dept.id_path like CONCAT (#{idPath},'%') 
	 and week.status = #{status} 
	 and week.create_by = #{createBy}

select `id`, `status` from cus_week_report
and id in
#{item}
and status =#{status}
