mapper.select:
		SELECT 

		FROM mes_bom



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

SELECT bom.`id` AS bomId, bom.`serialno` AS bomSerialno, bom.`name` AS bomName, dict.`name` AS typeName, dict.`id` AS typeId, bom.`version` AS version, bommaterial.serial_no AS bomMaterialserialNo, bommaterial.name AS bomMaterialname, bommaterial.specification AS bomMaterialSpecification, bom.`count` AS bomCount, bom.`image_no` AS imageNo, bom.`audit_sign` AS statusId, dica.`name` AS status FROM mes_bom bom LEFT JOIN cus_materiel bommaterial ON bommaterial.`id`=bom.`materiel_id` LEFT JOIN cus_dictionary dict ON dict.`id` = bom.`type` LEFT JOIN cus_dictionary dica ON dica.`id` = bom.`audit_sign`
and bom.id = #{id} and bom.`name` LIKE CONCAT ('%',#{bomName},'%') and bommaterial.`name` LIKE CONCAT ('%',#{proName},'%') and bom.`materiel_id` = #{proId} and bom.`image_no` LIKE CONCAT ('%',#{imageNo},'%') and bom.`audit_sign` = #{auditSign} and bom.use_status = #{useStatus} and bom.del_flag = #{delFlag}
order by ${sort} ${order}
order by bom.`id` desc
limit #{offset}, #{limit}

		SELECT 
		COUNT(*)
		FROM mes_bom bom
		LEFT JOIN cus_materiel bommaterial ON bommaterial.`id`=bom.`materiel_id`
		LEFT JOIN cus_dictionary dict ON dict.`id` = bom.`type`

				 and bom.id = #{id} 
			     and bom.`name` LIKE CONCAT ('%',#{bomName},'%')  
		  		 and bommaterial.`name` LIKE CONCAT ('%',#{proName},'%') 
		  		 and bom.`materiel_id` = #{proId} 
		  		 and bom.`image_no` LIKE CONCAT ('%',#{imageNo},'%') 
		  		 and bom.`audit_sign` = #{auditSign} 
		  		 and bom.use_status = #{useStatus} 
                 and  bom.del_flag = #{delFlag}
