mapper.select:
SELECT

FROM mes_craft



		order by ${sort} ${order}


		order by id desc



	limit #{offset}, #{limit}

SELECT
a.id as craftId,
a.code,
a.name,
a.remark,
a.audit_sign,

c.name as auditSignName,
a.use_status as useStatus,
case when a.use_status=1 then '启用'
when a.use_status=0 then '禁用'
end useStatusName,
a.version,
a.create_time as createTime,
a.create_by as createBy,
f.name as createName,
a.audit_id as auditId,
g.name as auditName
FROM mes_craft  a
LEFT JOIN cus_dictionary  c on a.audit_sign=c.id
LEFT JOIN sys_user  f on a.create_by=f.user_id
LEFT JOIN sys_user  g on a.audit_id =g.user_id

	 and a.id = #{id} 
	 and a.code = #{code} 
	 and a.name  like CONCAT('%',#{name},'%') 
	 and a.version = #{version} 
	 and a.remark = #{remark} 
	 and a.audit_id = #{auditId} 
	 and a.use_status = #{useStatus} 
	 and a.audit_sign = #{auditSign} 
	 and a.create_by = #{createBy} 
	 and a.create_time = #{createTime} 
	 and a.update_by = #{updateBy} 
	 and a.update_time = #{updateTime} 
	 and a.del_flag = #{delFlag} 



		order by ${sort} ${order}


		order by a.id desc



	limit #{offset}, #{limit}

SELECT
count(*)
FROM mes_craft  a
LEFT JOIN cus_dictionary  c on a.audit_sign=c.id
LEFT JOIN sys_user  f on a.create_by=f.user_id
LEFT JOIN sys_user  g on a.audit_id =g.user_id

	 and a.id = #{id} 
	 and a.code = #{code} 
	 and a.name  like CONCAT('%',#{name},'%') 
	 and a.version = #{version} 
	 and a.remark = #{remark} 
	 and a.audit_id = #{auditId} 
	 and a.use_status = #{useStatus} 
	 and a.audit_sign = #{auditSign} 
	 and a.create_by = #{createBy} 
	 and a.create_time = #{createTime} 
	 and a.update_by = #{updateBy} 
	 and a.update_time = #{updateTime} 
	 and a.del_flag = #{delFlag}

SELECT b.id as itemId, b.process_id as processId, e.name as processName, e.`code` as processCode, e.type, d.name as typeName, e.standard, b.demand, b.dept_id as deptId, a.name as deptName, b.operator, f.name as operatorName, b.auto_dispatch as autoDispatch, b.whether_outsource as whetherOutsource, b.whether_collect as whetherCollect, b.whether_examine as whetherExamine, b.device_id as deviceId, g.name as deviceName, b.man_hour as manHour, b.labour_price as labourPrice, b.total_hour as totalHour FROM mes_craft_item b LEFT JOIN mes_process e on b.process_id=e.id LEFT JOIN cus_dictionary d on e.type=d.id LEFT JOIN sys_dept a on b.dept_id=a.dept_id LEFT JOIN sys_user f on b.operator=f.user_id LEFT JOIN cus_device g on b.device_id=g.id LEFT JOIN mes_craft h on b.craft_id=h.id
and h.id = #{id}
order by ${sort} ${order}
order by b.id ASC
limit #{offset}, #{limit}
