mapper.select:
		SELECT 

		FROM mes_production_feeding_detail



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

SELECT detail.id AS id, detail.`materiel_id` AS materielId, material.serial_no AS materialSerialNo, material.name AS materialName, material.specification AS specification, unituom.id AS unitUomId, unituom.name AS unitUomName, detail.`batch_no` AS batchNo, detail.`plan_feeding` AS planFeedingCount,
detail.`out_count` AS outCount, detail.`scrap_count` AS scrapCount, facility.id AS defaultFacilityId, location.id AS defaultLocationId, facility.name as defaultFacilityName, location.name as defaultLocationName,
feeding.`feeding_no` AS feedingNo, feeding.`id` AS feedingId, headmaterial.`name` AS productName, headmaterial.specification AS productSpecification, feeding.`feeding_no` AS feedingNo, feeding.`create_time` AS createTime
order by ${sort} ${order}
order by detail.id desc
limit #{offset}, #{limit}

SELECT detail.id AS id, plan.`id` AS planId, plan.plan_no AS planNo, dept.dept_id AS deptId, dept.name AS deptName, feeding.`feeding_no` AS feedingNo, detail.`materiel_id` AS materielId, material.serial_no AS materialSerialNo, material.name AS materialName, material.specification AS specification, unituom.id AS unitUomId, unituom.name AS unitUomName, detail.`batch_no` AS batchNo, detail.`plan_feeding` AS planFeedingCount, detail.`out_count` AS outCount, detail.`scrap_count` AS scrapCount, feeding.`id` AS feedingId
order by ${sort} ${order}
order by detail.id desc
limit #{offset}, #{limit}
