mapper.select:
		SELECT

		FROM mes_production_feeding_detail



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

SELECT detail.id AS id, detail.`materiel_id` AS materielId, material.serial_no AS materialSerialNo, material.name AS materialName, material.specification AS specification, unituom.id AS unitUomId, unituom.name AS unitUomName, detail.`batch_no` AS batchNo, IFNULL(detail.`plan_feeding`,0) AS planFeedingCount,
IFNULL(detail.`out_count`,0) AS outCount, IFNULL(detail.`scrap_count`,0)AS scrapCount, facility.id AS defaultFacilityId, location.id AS defaultLocationId, facility.name as defaultFacilityName, location.name as defaultLocationName, detail.process_id AS processId, pro.name as processName, station.`id` AS stationId, station.name as stationName, detail.`is_collect` AS isCollect, IF(detail.`is_collect`=1,'是','否') AS isCollectName, feeding.`feeding_no` AS feedingNo, feeding.`id` AS feedingId, headmaterial.`name` AS productName, IFNULL(feeding.`is_quota`,0) AS isQuota, headmaterial.specification AS productSpecification, feeding.`feeding_no` AS feedingNo, feeding.`create_time` AS createTime
order by ${sort} ${order}
order by detail.id desc
limit #{offset}, #{limit}

SELECT detail.id AS id, plan.`id` AS planId, plan.plan_no AS planNo, dept.dept_id AS deptId, dept.name AS deptName, feeding.`feeding_no` AS feedingNo, detail.`materiel_id` AS materielId, material.serial_no AS materialSerialNo, material.name AS materialName, material.specification AS specification, unituom.id AS unitUomId, unituom.name AS unitUomName, detail.`batch_no` AS batchNo, IFNULL(detail.`plan_feeding`,0) AS planFeedingCount, IFNULL(detail.`out_count`,0) AS outCount, IFNULL(detail.`scrap_count`,0) AS scrapCount, feeding.`id` AS feedingId, /*委外投料*/ oc.`contract_code` AS contractCode, supplier.name AS supplierName, supplier.`id` AS supplierId
order by ${sort} ${order}
order by detail.id desc
limit #{offset}, #{limit}
