mapper.select:
SELECT typeId, typeName, id, `time`, UNIX_TIMESTAMP(`time`) AS times, `code`, materielId, serialNo, materielName, specification, unitUomName, batch, SUM(`count`) AS `count`, facilityName, way, locationName, sourceTypeName, 0 AS sign, sortNo, sourceCode FROM ( SELECT dicst.`id` AS typeId, dicst.`name` AS typeName, stock.id AS id, stock.create_time AS `time`, stock.inhead_code AS `code`, materiel.`id` AS materielId, materiel.`serial_no` AS serialNo, materiel.`name` AS materielName, materiel.`specification` AS specification, dic.`name` AS unitUomName, item.`batch` AS batch, item.`count` AS `count`, facility.`name` AS facilityName, location.`name` AS locationName, dict.`name` AS sourceTypeName, 'in' AS way, 1 AS sortNo, item.source_code AS sourceCode FROM scm_stock_in_item item LEFT JOIN scm_stock_in stock ON stock.id=item.inhead_id LEFT JOIN cus_materiel materiel ON materiel.id=item.materiel_id LEFT JOIN cus_dictionary dic ON dic.`id`=materiel.unit_uom LEFT JOIN cus_facility facility ON facility.id = item.warehouse LEFT JOIN cus_facility_location location ON location.id = item.wareh_location LEFT JOIN cus_dictionary dict ON dict.`id`=item.source_type LEFT JOIN cus_dictionary dicst ON dicst.`id`=stock.storage_type
and stock.storage_type = #{storageType} and stock.storage_type IN
#{item}
and stock.audit_sign = #{auditSign}
and item.`batch` = #{batch} and item.`materiel_id` = #{materielId} and DATE_FORMAT(stock.in_out_time,'%Y-%m-%d') >= DATE_FORMAT(#{startTime},'%Y-%m-%d') and DATE_FORMAT(stock.in_out_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d') AND materiel.is_lot=1
UNION ALL SELECT inspection.inspection_type AS typeId, dici.`name` AS typeName, inspection.id AS id, inspection.create_time AS `time`, inspection.inspection_no AS `code`, materiel.`id` AS materielId, materiel.`serial_no` AS serialNo, materiel.`name` AS materielName, materiel.`specification` AS specification, dic.`name` AS unitUomName, inspection.`batch_no` AS batch, inspection.inspection_count AS `count`, '' AS facilityName, '' AS locationName, dict.`name` AS sourceTypeName, 'in' AS way, 1 AS sortNo, inspection.source_no AS sourceCode FROM
view_inspection_contract_in viewi
view_inspection_contract_pro viewi
LEFT JOIN mes_material_inspection inspection ON viewi.id=inspection.id LEFT JOIN cus_materiel materiel ON materiel.id=inspection.materiel_id LEFT JOIN cus_dictionary dic ON dic.`id`=materiel.unit_uom LEFT JOIN cus_dictionary dict ON dict.`id`=inspection.source_type LEFT JOIN cus_dictionary dici ON dici.`id`=inspection.inspection_type
and inspection.inspection_type = #{inspectionType}
and inspection.status = #{auditSign}
and inspection.`materiel_id` = #{materielId} and DATE_FORMAT(inspection.create_time,'%Y-%m-%d') >= DATE_FORMAT(#{startTime},'%Y-%m-%d') and DATE_FORMAT(inspection.create_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d')
UNION ALL SELECT dico.`id` AS typeId, dico.`name` AS typeName, sout.id AS id, sout.`create_time` AS `time`, sout.`out_code` AS `code`, materiel.`id` AS materielId, materiel.`serial_no` AS serialNo, materiel.`name` AS materielName, materiel.`specification` AS specification, dic.`name` AS unitUomName, item.`batch` AS batch, item.count AS `count`, facility.`name` AS facilityName, location.`name` AS locationName, dict.`name` AS sourceTypeName, 'out' AS way, 1 AS sortNo, item.source_code AS sourceCode FROM scm_stock_out_item item LEFT JOIN scm_stock_out sout ON sout.id=item.out_id LEFT JOIN scm_stock stock ON stock.id=item.stock_id LEFT JOIN cus_facility facility ON facility.id = stock.warehouse LEFT JOIN cus_facility_location location ON location.id = stock.wareh_location LEFT JOIN cus_materiel materiel ON materiel.id=item.materiel_id LEFT JOIN cus_dictionary dic ON dic.`id`=materiel.unit_uom LEFT JOIN cus_dictionary dict ON dict.`id`=item.source_type LEFT JOIN cus_dictionary dico ON dico.`id`=sout.outbound_type
and sout.outbound_type = #{outboundType} and sout.outbound_type IN
#{item}
and sout.audit_sign = #{auditSign}
and item.`batch` = #{batch} and item.`materiel_id` = #{materielId} and DATE_FORMAT(sout.`out_time`,'%Y-%m-%d') >= DATE_FORMAT(#{startTime},'%Y-%m-%d') and DATE_FORMAT(sout.`out_time`,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d') AND materiel.is_lot=1
UNION ALL SELECT 278 AS typeId, '生产计划' AS typeName, plan.`id` AS id, plan.`create_time` AS `time`, plan.`plan_no` AS `code`, materiel.`id` AS materielId, materiel.`serial_no` AS serialNo, materiel.`name` AS materielName, materiel.`specification` AS specification, dic.`name` AS unitUomName, plan.`batch_no` AS batch, plan.plan_count AS `count`, '' AS facilityName, '' AS locationName, dict.`name` AS sourceTypeName, 'in' AS way, 1 AS sortNo, plan.source_no AS sourceCode FROM mes_production_plan plan LEFT JOIN cus_materiel materiel ON materiel.id=plan.materiel_id LEFT JOIN cus_dictionary dic ON dic.`id`=materiel.unit_uom LEFT JOIN cus_dictionary dict ON dict.`id`=plan.source_type
and plan.`materiel_id` = #{materielId} and DATE_FORMAT(plan.`create_time`,'%Y-%m-%d') >= DATE_FORMAT(#{startTime},'%Y-%m-%d') and DATE_FORMAT(plan.`create_time`,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d')
) a GROUP BY a.`id`,a.`typeId`
