mapper.select:
		SELECT 

		FROM scm_allot



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

SELECT allot.`id` AS id, allot.`allot_time` AS allotTime, allot.`allot_code` AS allotCode, materiel.`name` AS materielName, materiel.`serial_no` AS materielSerialno, materiel.`specification` AS specification, dicu.`name` AS unitName, dicu.`value` AS unitValue, detail.`count` AS allotCount, detail.`price` AS materielPrice, IFNULL(detail.`count`,0)*IFNULL(detail.`price`,0) AS amount, outlo.`name` AS outLocationName, outfac.`name` AS outFacilityName, inlo.`name` AS inLocationName, infac.`name` AS inFacilityName, stock.`batch` AS batch, person.`name` AS allotUserName, dica.`name` AS auditSignName, dica.`id` AS auditSign
order by ${sort} ${order}
order by allot.id desc
limit #{offset}, #{limit}
