mapper.select:
		SELECT 

		FROM scm_bank_transfer



                ORDER BY ${sort} ${order}


                ORDER BY id DESC



			limit #{offset}, #{limit}

select a.id as id , a.transfer_code as transferCode, a.transfer_date as transferDate, a.`transactor_id` as transactorId, b.name as transactorName, a.audit_sign AS auditSign, c.name as auditSignName, d.id as itemId , d.transfer_out_acc as transferOutAcc, e.account_number as transferOutAccName, d.transfer_in_acc as transferInAcc, f.account_number as transferInAccName, d.transfer_amount as transferAmount, d.settlement_type as settlementType, g.name as settlementTypeName, d.statement_number as statementNumber, d.remarks from scm_bank_transfer a LEFT JOIN sys_user b on a.`transactor_id`=b.user_id LEFT JOIN cus_dictionary c on a.audit_sign=c.id LEFT JOIN scm_bank_transfer_item d on a.id =d.transfer_id LEFT JOIN scm_fund_initialization e on d.transfer_out_acc=e.id LEFT JOIN scm_fund_initialization f on d.transfer_in_acc=f.id LEFT JOIN cus_dictionary g on d.settlement_type=g.id
AND id = #{id} and DATE_FORMAT(a.transfer_date,'%Y-%m-%d') >= DATE_FORMAT(#{startTime},'%Y-%m-%d') and DATE_FORMAT(a.transfer_date,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d') AND a.transfer_code = #{transferCode} AND a.audit_sign = #{auditSign}
ORDER BY ${sort} ${order}
ORDER BY id DESC
limit #{offset}, #{limit}
