mapper.select:
		select `id`,`head_id`,`body_id`,`code`,`document_type` from scm_inventory_plan_fitloss

		  		   and id = #{id} 
		  		   and head_id = #{headId} 
		  		   and body_id = #{bodyId} 
		  		   and code = #{code} 
		  		   and document_type = #{documentType} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

