mapper.select:
		SELECT 

		FROM scm_other_receivables



                ORDER BY ${sort} ${order}


                ORDER BY id DESC



			limit #{offset}, #{limit}

SELECT * from ( select a.sign, a.id, a.supplier_client_id as supplierClientId, IF(a.sign='QTYS',c.name,c.name) as clientName, IF(a.sign='QTYF',b.name,b.name) as supplierName,
a.code, a.amount, a.digest, a.dept_id as deptId, d.name as deptName, a.audit_sign as auditSign, e.name as auditSignName, a.audit_time as auditTime, a.auditor, f.name as auditorName, a.create_by as createBy, g.name as createByName, a.create_time as createTime from scm_other_receivables a LEFT JOIN cus_supplier b ON a.supplier_client_id=b.id LEFT JOIN cus_client c on a.supplier_client_id=c.id LEFT JOIN sys_dept d on a.dept_id=d.dept_id LEFT JOIN cus_dictionary e on a.audit_sign=e.id LEFT JOIN sys_user f on a.auditor=f.user_id LEFT JOIN sys_user g on a.create_by=g.user_id ) aa
and DATE_FORMAT(createTime,'%Y-%m-%d') >= DATE_FORMAT(#{startTime},'%Y-%m-%d') and DATE_FORMAT(createTime,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d') AND sign = #{sign} AND auditSign = #{auditSign} AND code = #{code} AND supplierClientId = #{clientSupplierId} AND supplierName Like CONCAT('%',#{supplierName},'%') AND clientName Like CONCAT('%',#{clientName},'%')
ORDER BY ${sort} ${order}
ORDER BY id DESC
limit #{offset}, #{limit}
