mapper.select:
		SELECT 

		FROM scm_payment_received_item



                ORDER BY ${sort} ${order}


                ORDER BY id DESC



			limit #{offset}, #{limit}

SELECT
sum(a.this_amount)
FROM
scm_payment_received_item a

	 and a.id<> #{id} 
	 and a.source_id =#{sourceId} 
	 and a.source_type = #{sourceType}

SELECT a.id, a.account_number as accountNumber, a.this_amount AS thisAmount FROM scm_payment_received_item a LEFT JOIN scm_payment_received b on b.id=a.payment_received_id
and DATE_FORMAT(b.create_time,'%Y-%m-%d') >= DATE_FORMAT(#{startTime},'%Y-%m-%d') and DATE_FORMAT(b.create_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d') AND b.sign = #{sign} AND b.audit_sign = #{auditSign} AND a.account_number IN
#{id}

SELECT a.id, b.create_time as createTime, b. a.account_number as accountNumber, a.this_amount AS thisAmount FROM scm_payment_received_item a LEFT JOIN scm_payment_received b on b.id=a.payment_received_id
and DATE_FORMAT(b.create_time,'%Y-%m-%d') >= DATE_FORMAT(#{startTime},'%Y-%m-%d') and DATE_FORMAT(b.create_time,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d') AND b.sign = #{sign} AND b.audit_sign = #{auditSign} AND a.account_number IN
#{id}
