mapper.select:
		SELECT 

		FROM scm_purchase_expense



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

SELECT * from ( SELECT a.id, a.invoice_date as invoiceDate, a.invoice_num as invoiceNum, a.supplier_id as supplierId, f.name as supplierName, a.audit_sign as auditSign, b.name as auditSignName, c.item_code as itemCode, j.name, j.code, j.type, d.name as typeName, c.unit, e.name as unitName, c.count, c.unit_price as unitPrice, c.tax_unit_price as taxUnitPrice, c.tax_rate as taxRate, c.amount, c.tax_amount as taxAmount, c.taxes from scm_purchase_expense a LEFT JOIN cus_supplier f on a.supplier_id =f.id LEFT JOIN cus_dictionary b on a.audit_sign =b.id LEFT JOIN scm_purchase_expense_item c on c.purchase_expense_id =a.id LEFT JOIN cus_expense j on c.expense_id=j.id LEFT JOIN cus_dictionary d on j.type =d.id LEFT JOIN cus_dictionary e on c.unit =e.id ) aa
and supplierName LIKE CONCAT('%',#{supplierName},'%') and DATE_FORMAT(applicantTime,'%Y-%m-%d') >= DATE_FORMAT(#{startTime},'%Y-%m-%d') and DATE_FORMAT(applicantTime,'%Y-%m-%d') <= DATE_FORMAT(#{endTime},'%Y-%m-%d') and invoiceNum =#{invoiceNum} and name LIKE CONCAT('%',#{expenceName},'%')
order by ${sort} ${order}
order by id desc
limit #{offset}, #{limit}
