mapper.select:
		SELECT 

		FROM scm_salesbill



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

SELECT item.id AS itemId, item.salesbill_id AS id, bill.invoice_date AS invoiceDate, client.name AS clientName, bill.invoice_type AS invoiceType, dici.`name` AS invoiceyTypeName, bill.invoice_num AS invoiceNum, materiel.serial_no AS materielSerialNo, materiel.name AS materielName, materiel.specification AS specification, item.count AS count, item.unit_price AS unitPrice, item.tax_unit_price AS taxUnitPrice, item.tax_rate AS taxRate, item.amount AS amount, item.taxes AS taxes, item.tax_amount AS taxAmount, bill.audit_sign AS auditSign, dica.`name` AS auditSignName, item.source_type AS sourceType, dict.`name` AS sourceTypeName, item.source_code AS sourceCode
order by ${sort} ${order}
order by item.salesbill_id desc,item.id
limit #{offset}, #{limit}

mapper.sql:
FROM scm_salesbill_item item
LEFT JOIN scm_salesbill bill ON bill.id = item.salesbill_id
LEFT JOIN cus_client client ON client.id = bill.client_id
LEFT JOIN cus_dictionary dici ON  dici.id = bill.invoice_type
LEFT JOIN cus_materiel materiel ON materiel.`id`=item.`materiel_id`
LEFT JOIN cus_dictionary dica ON  dica.id = bill.audit_sign
LEFT JOIN cus_dictionary dict ON  dict.id = item.source_type

     and bill.bill_code = #{billCode} 
     and client.name LIKE #{clientName} 
     and materiel.name LIKE #{materielName} 
     and  DATE_FORMAT(item.create_time,'%Y-%m-%d') >=  DATE_FORMAT(#{startTime},'%Y-%m-%d')
      and  DATE_FORMAT(item.create_time,'%Y-%m-%d') <=   DATE_FORMAT(#{endTime},'%Y-%m-%d')

     and item.salesbill_id = #{id} 
     and item.id = #{itemId} 
     and bill.audit_sign = #{auditSign} 
     and bill.auditor = #{auditor} 
     and bill.audit_time = #{auditTime} 
     and bill.create_by = #{createBy} 
     and bill.create_time = #{createTime} 
     and bill.update_by = #{updateBy} 
     and bill.update_time = #{updateTime} 
     and bill.del_flag = #{delFlag}
