mapper.select:
		SELECT 

		FROM scm_salescontract



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

SELECT item.`id`, sales.`id` AS salesContractId, sales.contract_date AS contractDate, sales.`contract_code` AS `contractCode`, dict.`name` AS contractTypeName, IF(sales.close_status=0,'未关闭','关闭') AS closeStatusName, client.`name` AS clientName, salesUser.`name` AS salesPersonName, salesUserDept.name AS salesPersonDeptName, IF(item.is_check=0,'是','否') AS isCheck, materiel.`id` AS materielId, materiel.serial_no AS materielSerialNo, materiel.name AS materielName, materiel.specification AS specification, unitUom.name AS unitUomName, item.`count` AS count, item.`tax_rate` AS taxRate, item.`unit_price` AS unitPrice, item.`tax_unit_price` AS taxUnitPrice, item.`amount` AS amount, item.`tax_amount` AS taxAmount, item.`delivery_date` AS deliveryDate, dica.`name` AS auditSignName, sales.`contract_code` AS code, sales.`create_time` AS createTime
order by ${sort} ${order}
order by sales.`id` desc,item.`id`
limit #{offset}, #{limit}

mapper.sql:
FROM
scm_salescontract_item item
LEFT JOIN scm_salescontract sales ON sales.id = item.salesContract_id
LEFT JOIN cus_dictionary dict ON  dict.id = sales.contract_type
LEFT JOIN cus_client client ON client.id = sales.client_id
LEFT JOIN sys_user salesUser ON salesUser.user_id = sales.sales_person
LEFT JOIN sys_dept salesUserDept ON salesUserDept.dept_id = sales.sales_person_dept
LEFT JOIN cus_materiel materiel ON materiel.`id`=item.`materiel_id`
LEFT JOIN cus_dictionary unitUom ON unitUom.id = materiel.`unit_uom`
LEFT JOIN cus_dictionary dica ON dica.id = sales.audit_sign

     and item.id = #{id} 
     and  DATE_FORMAT(sales.create_time,'%Y-%m-%d') >=  DATE_FORMAT(#{startTime},'%Y-%m-%d')
      and  DATE_FORMAT(sales.create_time,'%Y-%m-%d') <=   DATE_FORMAT(#{endTime},'%Y-%m-%d')
     and sales.contract_code = #{contractCode} 
     and CONCAT(materiel.`name`,client.`name`) LIKE #{fuzzyQuery} 

     and contract_type = #{contractType} 
     and  DATE_FORMAT(sales.contract_date,'%Y-%m-%d') >=  DATE_FORMAT(#{contractStartTime},'%Y-%m-%d')
      and  DATE_FORMAT(sales.contract_date,'%Y-%m-%d') <=   DATE_FORMAT(#{contractEndTime},'%Y-%m-%d')
     and client.`name` LIKE #{clientName} 
     and client.`id` = #{clientId} 
     and materiel.`name` LIKE #{materielName} 
     and materiel.`specification` LIKE #{specification} 
     and sales.sales_person = #{salesPerson} 
     and sales.sales_person_dept = #{salesPersonDept} 
     and sales.audit_sign = #{auditSign} 
     and sales.create_by = #{createBy} 
     and DATE_FORMAT(sales.create_time,'%Y-%m-%d') = DATE_FORMAT(#{createTime},'%Y-%m-%d') 

     and  DATE_FORMAT(sales.create_by,'%Y-%m-%d') >=  DATE_FORMAT(#{createStartTime},'%Y-%m-%d')
      and  DATE_FORMAT(sales.create_by,'%Y-%m-%d') <=   DATE_FORMAT(#{createEndTime},'%Y-%m-%d')
     and CONCAT(materiel.`name`,materiel.`serial_no`,sales.`contract_code`,client.`name`) LIKE #{fuzzyInquire} 

     and update_by = #{updateBy} 
     and update_time = #{updateTime} 
     and del_flag = #{delFlag} 
     and close_status = #{closeStatus}
