mapper.sql:
		FROM scm_stock_analysis analysis
		LEFT JOIN cus_materiel materiel ON materiel.id=analysis.materiel_id

                             AND analysis.id = #{id} 
                             AND DATE_FORMAT(analysis.period,'%Y-%m-%d') = DATE_FORMAT(#{period},'%Y-%m-%d') 
                             AND analysis.materiel_id = #{materielId} 
                             AND analysis.batch = #{batch} 
                             AND analysis.initial_count = #{initialCount} 
                             AND analysis.initial_amount = #{initialAmount} 
                             AND analysis.in_count = #{inCount} 
                             AND analysis.in_amount = #{inAmount} 
                             AND analysis.out_count = #{outCount} 
                             AND analysis.out_amount = #{outAmount} 
                             AND analysis.final_count = #{finalCount} 
                             AND analysis.final_amount = #{finalAmount} 
							 AND analysis.is_close = #{isClose} 
                             AND analysis.create_by = #{createBy} 
                             AND analysis.create_time = #{createTime} 
                             AND analysis.update_by = #{updateBy} 
                             AND analysis.update_time = #{updateTime} 
                             AND analysis.del_flag = #{delFlag} 

							 AND materiel.valuation_method = #{valuationMethod}

mapper.select:
SELECT analysis.`id`, analysis.`period`, analysis.`materiel_id`, analysis.`batch`, SUM(analysis.`initial_count`) AS initial_count, SUM(analysis.`initial_amount`) AS initial_amount, SUM(analysis.`in_count`) AS in_count, SUM(analysis.`in_amount`) AS in_amount, SUM(analysis.`out_count`) AS out_count, SUM(analysis.`out_amount`) AS out_amount, SUM(analysis.`final_count`) AS final_count, SUM(analysis.`final_amount`) AS final_amount, analysis.`is_close`, analysis.`create_by`, analysis.`create_time`, analysis.`update_by`, analysis.`update_time`, analysis.`del_flag`
ORDER BY ${sort} ${order}
GROUP BY analysis.materiel_id ORDER BY analysis.id DESC
limit #{offset}, #{limit}

