mapper.select:
SELECT analysis.`id` AS id, LEFT(analysis.`period`,7) AS period, UNIX_TIMESTAMP(analysis.`period`) AS times, mtype.`name` AS materielTypeName, analysis.`materiel_id` AS materielId, materiel.serial_no AS materielSerialNo, materiel.name AS materielName, materiel.specification AS specification, unitUom.name AS unitUomName, materiel.valuation_method AS valuationMethod, analysis.`batch` AS batch, IFNULL(analysis.`initial_count`,0) AS initialCount, IFNULL(analysis.`initial_amount`,0) AS initialAmount, IFNULL(analysis.`in_count`,0) AS inCount, IFNULL(analysis.`in_amount`,0) AS inAmount, IFNULL(analysis.`out_count`,0) AS outCount, IFNULL(analysis.`out_amount`,0) AS outAmount, IFNULL(analysis.`final_count`,0) AS finalCount, IFNULL(analysis.`final_amount`,0) AS finalAmount, 0 AS sign, 0 AS sortNo, analysis.`is_close` AS isClose
ORDER BY ${sort} ${order}
ORDER BY analysis.`materiel_id`,analysis.`initial_count` DESC
limit #{offset}, #{limit}

SELECT analysis.`id` AS id, LEFT(analysis.`period`,7) AS period, UNIX_TIMESTAMP(analysis.`period`) AS times, analysis.`materiel_id` AS materielId, materiel.serial_no AS materielSerialNo, materiel.name AS materielName, materiel.specification AS specification, unitUom.name AS unitUomName, materiel.valuation_method AS valuationMethod, SUM(analysis.`initial_count`) AS initialCount, SUM(analysis.`initial_amount`) AS initialAmount, SUM(analysis.`in_count`) AS inCount, SUM(analysis.`in_amount`) AS inAmount, SUM(analysis.`out_count`) AS outCount, SUM(analysis.`out_amount`) AS outAmount, SUM(analysis.`final_count`) AS finalCount, SUM(analysis.`final_amount`) AS finalAmount, analysis.`is_close` AS isClose
ORDER BY ${sort} ${order}
GROUP BY analysis.`materiel_id`,analysis.`period` ORDER BY analysis.`materiel_id`,analysis.`initial_count` DESC
limit #{offset}, #{limit}

mapper.sql:
FROM scm_stock_analysis analysis
LEFT JOIN cus_materiel materiel ON materiel.`id`=analysis.`materiel_id`
LEFT JOIN cus_materiel_type mtype ON mtype.`id`=materiel.`type`
LEFT JOIN cus_dictionary unitUom ON unitUom.id = materiel.`unit_uom`

	 AND analysis.id = #{id} 
	 AND materiel.valuation_method = #{valuationMethod} 
	 AND DATE_FORMAT(analysis.period,'%Y-%m-%d') = DATE_FORMAT(#{period},'%Y-%m-%d') 
	  and  DATE_FORMAT(analysis.period,'%Y-%m-%d') >=  DATE_FORMAT(#{startTime},'%Y-%m-%d')
	  and  DATE_FORMAT(analysis.period,'%Y-%m-%d') <=  DATE_FORMAT(#{endTime},'%Y-%m-%d')
	 AND analysis.materiel_id = #{materielId} 
	 AND materiel.type = #{materielType} 
	 AND materiel.`name` LIKE #{materielName} 
	 AND analysis.batch = #{batch} 
	 AND analysis.initial_count = #{initialCount} 
	 AND analysis.initial_amount = #{initialAmount} 
	 AND analysis.in_count = #{inCount} 
	 AND analysis.in_amount = #{inAmount} 
	 AND analysis.out_count = #{outCount} 
	 AND analysis.out_amount = #{outAmount} 
	 AND analysis.final_count = #{finalCount} 
	 AND analysis.final_amount = #{finalAmount} 
	 AND analysis.is_close = #{isClose} 
	 AND analysis.create_by = #{createBy} 
	 AND analysis.create_time = #{createTime} 
	 AND analysis.update_by = #{updateBy} 
	 AND analysis.update_time = #{updateTime} 
	 AND analysis.del_flag = #{delFlag}
