mapper.select:
SELECT

FROM scm_stock_in_item



		order by ${sort} ${order}


		order by id desc



	limit #{offset}, #{limit}

select b.id as headId, a.id as bodyId, b.in_time as inTime, b.inhead_code as inheadCode, b.source_company as sourceCompanyId, c.name as sourceCompanyName, d.serial_no as serialno, d.name as proName, d.specification as model, d.unit_uom as unit, e.name as unitName, a.pro_count as proCount, a.pro_price as proPrice, a.pro_amount as proAmount, a.warehouse, f.name as warehouseName, a.expense, a.cost, a.wareh_location as warehLocation, g.name as warehLocationName, a.source_singletype as sourceSingletype, h.name as sourceSingletypeName, a.source_singlecode as sourceSinglecode, b.audit_sign as auditSign, i.name as auditSignName from scm_stock_in_item a LEFT JOIN scm_stock_in b ON a.inhead_id=b.id LEFT JOIN cus_supplier c ON b.source_company=c.id LEFT JOIN cus_materiel d ON a.pro_id=d.id LEFT JOIN cus_dictionary e ON d.unit_uom=e.id LEFT JOIN cus_facility f ON a.warehouse=f.id LEFT JOIN cus_facility_location g ON a.wareh_location=g.id LEFT JOIN cus_dictionary h ON a.source_singletype=h.id LEFT JOIN cus_dictionary i ON b.audit_sign=i.id
and ((c.name like CONCAT('%',#{inDatas},'%'))or(d.name like CONCAT('%',#{inDatas},'%'))or(d.serialno like CONCAT('%',#{inDatas},'%'))or(b.inhead_code like CONCAT('%',#{inDatas},'%')))
and d.name like CONCAT('%',#{productName},'%')
and b.in_time >= #{startTime} and b.in_time <= #{endTime}
and b.source_company = #{sourceCompanyId}
and d.type = #{proType} and a.pro_batch = #{proBatch}
and b.audit_sign = #{auditSign}
and b.operator = #{operator}
and b.create_by = #{createBy} and b.create_time = #{createTime} and b.storage_type = #{storageType}
order by ${sort} ${order}
order by a.id desc
limit #{offset}, #{limit}

select count(*)
from scm_stock_in_item  a
LEFT JOIN  scm_stock_in  b  ON a.inhead_id=b.id
LEFT JOIN  cus_supplier  c  ON b.source_company=c.id
LEFT JOIN  cus_materiel d  ON a.pro_id=d.id
LEFT JOIN  cus_dictionary e  ON d.unit_uom=e.id
LEFT JOIN  cus_facility f  ON a.warehouse=f.id
LEFT JOIN  cus_facility_location g  ON a.wareh_location=g.id
LEFT JOIN  cus_dictionary h  ON a.source_singletype=h.id
LEFT JOIN  cus_dictionary i  ON b.audit_sign=i.id

	 and ((c.name like  CONCAT('%',#{inDatas},'%'))or(d.name like CONCAT('%',#{inDatas},'%'))or(d.serialno like CONCAT('%',#{inDatas},'%'))or(b.inhead_code like CONCAT('%',#{inDatas},'%')))

	 and  d.name like CONCAT('%',#{productName},'%')

	 and b.in_time >= #{inTime}
	 and b.in_time <= #{inTime}

	 and b.source_company = #{sourceCompanyId}

	 and d.type = #{proType}
	 and a.pro_batch = #{proBatch}

	 and b.audit_sign = #{auditSign}

	 and b.operator = #{operator}

	 and b.create_by = #{createBy}
	 and b.create_time = #{createTime}
	 and b.storage_type = #{storageType}

mapper.update:
UPDATE
	scm_stock_in_item a
set
    a.expense=#{expense}
WHERE
	a.inhead_id =#{stockInId}

UPDATE scm_stock_in_item




				when id=#{item.id} then #{item.unitPrice}


				when id=#{item.id} then scm_stock_in_item.unit_price






				when id=#{item.id} then #{item.amount}


				when id=#{item.id} then scm_stock_in_item.amount






				when id=#{item.id} then #{item.expense}


				when id=#{item.id} then scm_stock_in_item.expense





WHERE id IN

	#{item.id}

mapper.select:
SELECT DISTINCT
a.id, a.inhead_id, a.`count`, a.unit_price, a.amount, a.expense, a.cost, a.materialId_count, a.account_source FROM scm_stock_in_item a LEFT JOIN scm_stock_in b on a.inhead_id=b.id WHERE b.id in
#{id}
