mapper.select:
		select `id`,`name`,`province`,`city`,`district`,`address`,`telephone`,`linkman`,`mobile`,`email`,`zip_code`,`fax`,`job`,`qq`,`url`,`company_introduction`,`create_by`,`create_time`,`update_by`,`update_time`,`del_flag` from sys_company

		  		   and id = #{id} 
		  		   and name = #{name} 
		  		   and address = #{address} 
		  		   and telephone = #{telephone} 
		  		   and linkman = #{linkman} 
		  		   and mobile = #{mobile} 
		  		   and email = #{email} 
		  		   and zip_code = #{zipCode} 
		  		   and fax = #{fax} 
		  		   and job = #{job} 
		  		   and qq = #{qq} 
		  		   and url = #{url} 
		  		   and company_introduction = #{companyIntroduction} 
		  		   and create_by = #{createBy} 
		  		   and create_time = #{createTime} 
		  		   and update_by = #{updateBy} 
		  		   and update_time = #{updateTime} 
		  		   and del_flag = #{delFlag} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

