select count(*) from sys_user

	  and user_id = #{userId} 
	  and username = #{username} 
	  and name = #{name} 
	  and CONCAT(name,mobile) LIKE CONCAT('%',CONCAT(#{query},'%')) 
	  and password = #{password} 
	  and dept_id = #{deptId} 
	  and email = #{email} 
	  and mobile = #{mobile} 
	  and status = #{status} 
	  and create_by = #{createBy} 
	  and create_time = #{createTime} 
	  and update_time = #{updateTime} 
	  and update_by = #{updateBy} 
	  and del_flag = #{delFlag} 
	  and sex = #{sex} 
	  and birth = #{birth} 
	  and pic_id = #{picId} 
	  and live_address = #{liveAddress} 
	  and hobby = #{hobby} 
	  and province = #{province} 
	  and city = #{city} 
	  and district = #{district} 
	  and groupstr = #{groupstr}

		SELECT
		sysuser.`user_id` AS userId,
		sysuser.`username` AS userName,
		sysuser.`name` AS NAME,
		sysuser.`password` AS PASSWORD,
		sysuser.`dept_id` AS deptId,
		sysuser.`email` AS email,
		sysuser.`mobile` AS mobile,
		sysuser.`status` AS STATUS,
		sysuser.`create_by`,
		sysuser.`create_time`,
		sysuser.`update_time`,
		sysuser.`update_by`,
		sysuser.`del_flag`,
		sysuser.`sex` AS sex,
		sysuser.`birth` AS birth,
		sysuser.`pic_id` AS picId,
		sysuser.`live_address` AS liveAddress,
		sysuser.`hobby` AS hobby,
		sysuser.`province` AS province,
		sysuser.`city` AS city,
		sysuser.`district` AS district,
		sysuser.`groupstr` AS groupStr,
		sysuser.`isapp` AS isApp,
		sysuser.`isfirst` AS isFirst,
		dept.`name` as deptName,

        IF(sysuser.status=1,'启用','禁用') AS statusName
		FROM
		sys_user sysuser
		LEFT JOIN sys_dept dept on dept.dept_id = sysuser.dept_id

			 and sysuser.user_id = #{userId} 

			 and sysuser.name LIKE CONCAT(#{username},'%')
			 and CONCAT(sysuser.name,sysuser.mobile) LIKE CONCAT('%',CONCAT(#{query},'%')) 
			 and sysuser.password = #{password} 

				 and sysuser.dept_id = #{deptId} 


				 and dept.id_path like CONCAT ('%/',#{deptId},'/%') 

			 and sysuser.email = #{email} 
			 and sysuser.mobile = #{mobile} 
			 and sysuser.status = #{status} 
			 and sysuser.create_by = #{createBy} 
			 and sysuser.create_time = #{createTime} 
			 and sysuser.update_time = #{updateTime} 
			 and sysuser.update_by = #{updateBy} 
			 and sysuser.del_flag = #{delFlag} 
			 and sysuser.sex = #{sex} 
			 and sysuser.birth = #{birth} 
			 and sysuser.pic_id = #{picId} 
			 and sysuser.live_address = #{liveAddress} 
			 and sysuser.hobby = #{hobby} 
			 and sysuser.province = #{province} 
			 and sysuser.city = #{city} 
			 and sysuser.district = #{district} 
			 and sysuser.groupstr = #{groupstr} 



				order by ${sort} ${order}


				order by user_id desc



			limit #{offset}, #{limit}

SELECT
count(*)
FROM
sys_user sysuser
LEFT JOIN sys_dept dept on dept.dept_id = sysuser.dept_id

	 and sysuser.user_id = #{userId} 

	 and sysuser.name LIKE CONCAT(#{username},'%')
	 and CONCAT(sysuser.name,sysuser.mobile) LIKE CONCAT('%',CONCAT(#{query},'%')) 
	 and sysuser.password = #{password} 

		 and sysuser.dept_id = #{deptId} 


		 and dept.id_path like CONCAT ('%/',#{deptId},'/%') 

	 and sysuser.email = #{email} 
	 and sysuser.mobile = #{mobile} 
	 and sysuser.status = #{status} 
	 and sysuser.create_by = #{createBy} 
	 and sysuser.create_time = #{createTime} 
